import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Grid, 
  Card, 
  CardContent, 
  Paper,
  Button,
  Divider,
  LinearProgress,
  IconButton,
  Tooltip
} from '@mui/material';
import {
  People as PeopleIcon,
  Home as HomeIcon,
  Message as MessageIcon,
  AttachMoney as MoneyIcon,
  TrendingUp as TrendingUpIcon,
  Refresh as RefreshIcon,
  ArrowForward as ArrowForwardIcon,
  PersonAdd as PersonAddIcon,
  AddHome as AddHomeIcon,
  Email as EmailIcon
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import api from '../../services/api';

const StatCard = ({ title, value, icon: Icon, color, trend, trendText }) => (
  <Card elevation={2}>
    <CardContent>
      <Box display="flex" justifyContent="space-between" alignItems="center">
        <Box>
          <Typography color="textSecondary" variant="subtitle2" gutterBottom>
            {title}
          </Typography>
          <Typography variant="h4" component="div">
            {value}
          </Typography>
          {trend && (
            <Box display="flex" alignItems="center" mt={1}>
              <Typography 
                variant="caption" 
                color={trend > 0 ? 'success.main' : 'error.main'}
                display="flex"
                alignItems="center"
              >
                {trend > 0 ? '↑' : '↓'} {Math.abs(trend)}%
                <Box component="span" ml={0.5} color="text.secondary">
                  {trendText}
                </Box>
              </Typography>
            </Box>
          )}
        </Box>
        <Box
          sx={{
            backgroundColor: `${color}.light`,
            color: `${color}.dark`,
            borderRadius: '12px',
            height: '48px',
            width: '48px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}
        >
          <Icon fontSize="medium" />
        </Box>
      </Box>
    </CardContent>
  </Card>
);

const QuickAction = ({ icon: Icon, title, subtitle, color, onClick }) => (
  <Card 
    elevation={0} 
    sx={{ 
      border: '1px solid', 
      borderColor: 'divider',
      transition: 'all 0.3s ease',
      '&:hover': {
        transform: 'translateY(-4px)',
        boxShadow: 3,
        cursor: 'pointer',
        borderColor: `${color}.main`
      }
    }}
    onClick={onClick}
  >
    <CardContent>
      <Box display="flex" alignItems="center">
        <Box
          sx={{
            backgroundColor: `${color}.light`,
            color: `${color}.dark`,
            borderRadius: '12px',
            height: '48px',
            width: '48px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            mr: 2
          }}
        >
          <Icon />
        </Box>
        <Box>
          <Typography variant="subtitle1" fontWeight="medium">
            {title}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {subtitle}
          </Typography>
        </Box>
      </Box>
    </CardContent>
  </Card>
);

const AdminOverview = () => {
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalProperties: 0,
    totalMessages: 0,
    totalRevenue: 0,
    userGrowth: 0,
    propertyGrowth: 0,
    revenueGrowth: 0,
    loading: true
  });
  
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStats = async () => {
      try {
        // In a real app, you would fetch this from your API
        // const response = await api.get('/admin/stats');
        // setStats({ ...response.data, loading: false });
        
        // Mock data for now
        setTimeout(() => {
          setStats({
            totalUsers: 1245,
            totalProperties: 856,
            totalMessages: 342,
            totalRevenue: 12500,
            userGrowth: 12.5,
            propertyGrowth: 8.2,
            revenueGrowth: 5.7,
            loading: false
          });
        }, 500);
      } catch (error) {
        console.error('Error fetching stats:', error);
        setStats(prev => ({ ...prev, loading: false }));
      }
    };

    fetchStats();
  }, []);

  const handleRefresh = () => {
    setStats(prev => ({ ...prev, loading: true }));
    // Refetch logic would go here
    setTimeout(() => {
      setStats(prev => ({
        ...prev,
        loading: false
      }));
    }, 500);
  };

  if (stats.loading) {
    return (
      <Box sx={{ width: '100%', p: 3 }}>
        <LinearProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h4" component="h1">
          Admin Dashboard
        </Typography>
        <Box>
          <Tooltip title="Refresh">
            <IconButton onClick={handleRefresh}>
              <RefreshIcon />
            </IconButton>
          </Tooltip>
        </Box>
      </Box>

      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard 
            title="Total Users" 
            value={stats.totalUsers.toLocaleString()} 
            icon={PeopleIcon} 
            color="primary"
            trend={stats.userGrowth}
            trendText="vs last month"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard 
            title="Total Properties" 
            value={stats.totalProperties.toLocaleString()} 
            icon={HomeIcon} 
            color="secondary"
            trend={stats.propertyGrowth}
            trendText="vs last month"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard 
            title="Total Messages" 
            value={stats.totalMessages.toLocaleString()} 
            icon={MessageIcon} 
            color="info"
            trend={-3.1}
            trendText="vs last week"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard 
            title="Total Revenue" 
            value={`$${stats.totalRevenue.toLocaleString()}`} 
            icon={MoneyIcon} 
            color="success"
            trend={stats.revenueGrowth}
            trendText="vs last month"
          />
        </Grid>
      </Grid>

      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 3, height: '100%' }}>
            <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
              <Typography variant="h6">Quick Stats</Typography>
              <Button 
                size="small" 
                endIcon={<ArrowForwardIcon />}
                onClick={() => navigate('/dashboard/admin/analytics')}
              >
                View Analytics
              </Button>
            </Box>
            <Box height={300} display="flex" alignItems="center" justifyContent="center">
              <Typography color="text.secondary">
                Analytics chart will be displayed here
              </Typography>
            </Box>
          </Paper>
        </Grid>
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 3, height: '100%' }}>
            <Typography variant="h6" gutterBottom>System Status</Typography>
            <Box mb={3}>
              <Box display="flex" justifyContent="space-between" mb={1}>
                <Typography variant="body2">Server Load</Typography>
                <Typography variant="body2">24%</Typography>
              </Box>
              <LinearProgress variant="determinate" value={24} color="primary" />
            </Box>
            <Box mb={3}>
              <Box display="flex" justifyContent="space-between" mb={1}>
                <Typography variant="body2">Database</Typography>
                <Typography variant="body2" color="success.main">Connected</Typography>
              </Box>
              <LinearProgress variant="determinate" value={100} color="success" />
            </Box>
            <Box>
              <Box display="flex" justifyContent="space-between" mb={1}>
                <Typography variant="body2">Storage</Typography>
                <Typography variant="body2">65% used</Typography>
              </Box>
              <LinearProgress variant="determinate" value={65} color="warning" />
            </Box>
          </Paper>
        </Grid>
      </Grid>

      <Typography variant="h6" gutterBottom>Quick Actions</Typography>
      <Grid container spacing={3}>
        <Grid item xs={12} sm={6} md={3}>
          <QuickAction
            icon={PersonAddIcon}
            title="Add New User"
            subtitle="Create a new user account"
            color="primary"
            onClick={() => navigate('/dashboard/admin/users/new')}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <QuickAction
            icon={AddHomeIcon}
            title="Add Property"
            subtitle="Create a new property listing"
            color="secondary"
            onClick={() => navigate('/dashboard/admin/properties/new')}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <QuickAction
            icon={EmailIcon}
            title="View Messages"
            subtitle="Check user inquiries"
            color="info"
            onClick={() => navigate('/dashboard/admin/messages')}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <QuickAction
            icon={TrendingUpIcon}
            title="View Reports"
            subtitle="Generate and view reports"
            color="success"
            onClick={() => navigate('/dashboard/admin/reports')}
          />
        </Grid>
      </Grid>
    </Box>
  );
};

export default AdminOverview;
