import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FiMail, FiSearch, FiChevronRight, FiUser, FiClock } from 'react-icons/fi';

const Messages = () => {
  const [activeThread, setActiveThread] = useState(null);
  const [messages, setMessages] = useState([]);
  const [threads, setThreads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newMessage, setNewMessage] = useState('');
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    // Mock data - replace with actual API call
    const fetchThreads = () => {
      setTimeout(() => {
        const mockThreads = [
          {
            id: 1,
            user: {
              name: 'John Doe',
              avatar: '',
              lastSeen: '2h ago'
            },
            property: 'Modern Apartment in Bole',
            lastMessage: 'Hello, is this property still available?',
            unread: 2,
            timestamp: '10:30 AM'
          },
          {
            id: 2,
            user: {
              name: 'Real Estate Agency',
              avatar: '',
              lastSeen: '1d ago'
            },
            property: 'Villa in Kazanchis',
            lastMessage: 'We have scheduled a viewing for tomorrow',
            unread: 0,
            timestamp: 'Yesterday'
          },
          {
            id: 3,
            user: {
              name: 'Sarah Johnson',
              avatar: '',
              lastSeen: '3d ago'
            },
            property: 'Office Space in Bole',
            lastMessage: 'Thanks for the information!',
            unread: 0,
            timestamp: 'Monday'
          }
        ];
        setThreads(mockThreads);
        setLoading(false);
      }, 500);
    };

    fetchThreads();
  }, []);

  const handleThreadClick = (threadId) => {
    setActiveThread(threadId);
    // In a real app, fetch messages for this thread
    setMessages([
      { id: 1, sender: 'them', text: 'Hello, is this property still available?', time: '10:30 AM' },
      { id: 2, sender: 'you', text: 'Yes, it is still available. Would you like to schedule a viewing?', time: '10:35 AM' },
      { id: 3, sender: 'them', text: 'That would be great! When are you available?', time: '10:36 AM' },
    ]);
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;
    
    // In a real app, send message to the API
    const newMsg = {
      id: messages.length + 1,
      sender: 'you',
      text: newMessage,
      time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
    };
    
    setMessages([...messages, newMsg]);
    setNewMessage('');
  };

  const filteredThreads = threads.filter(thread => 
    thread.user.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    thread.property.toLowerCase().includes(searchQuery.toLowerCase())
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-gray-500">Loading messages...</div>
      </div>
    );
  }

  return (
    <div className="flex h-[calc(100vh-8rem)] bg-white rounded-lg shadow overflow-hidden">
      {/* Sidebar */}
      <div className={`${activeThread ? 'hidden md:block md:w-1/3' : 'w-full md:w-1/3'} border-r border-gray-200 flex flex-col`}>
        <div className="p-4 border-b border-gray-200">
          <h2 className="text-lg font-medium text-gray-900">Messages</h2>
          <div className="mt-2 relative rounded-md shadow-sm">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <FiSearch className="h-4 w-4 text-gray-400" />
            </div>
            <input
              type="text"
              className="focus:ring-blue-500 focus:border-blue-500 block w-full pl-10 sm:text-sm border-gray-300 rounded-md"
              placeholder="Search messages"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
        </div>
        
        <div className="flex-1 overflow-y-auto">
          {filteredThreads.length === 0 ? (
            <div className="p-4 text-center text-gray-500">No messages found</div>
          ) : (
            <ul className="divide-y divide-gray-200">
              {filteredThreads.map((thread) => (
                <li 
                  key={thread.id}
                  className={`px-4 py-4 hover:bg-gray-50 cursor-pointer ${activeThread === thread.id ? 'bg-blue-50' : ''}`}
                  onClick={() => handleThreadClick(thread.id)}
                >
                  <div className="flex items-center space-x-4">
                    <div className="flex-shrink-0">
                      {thread.user.avatar ? (
                        <img className="h-10 w-10 rounded-full" src={thread.user.avatar} alt="" />
                      ) : (
                        <div className="h-10 w-10 rounded-full bg-gray-200 flex items-center justify-center">
                          <FiUser className="h-5 w-5 text-gray-500" />
                        </div>
                      )}
                    </div>
                    <div className="flex-1 min-w-0">
                      <div className="flex justify-between">
                        <p className="text-sm font-medium text-gray-900 truncate">
                          {thread.user.name}
                        </p>
                        <p className="text-xs text-gray-500">{thread.timestamp}</p>
                      </div>
                      <p className="text-sm text-gray-600 truncate">
                        {thread.property}
                      </p>
                      <div className="flex justify-between items-center mt-1">
                        <p className="text-xs text-gray-500 truncate">
                          {thread.lastMessage}
                        </p>
                        {thread.unread > 0 && (
                          <span className="inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                            {thread.unread}
                          </span>
                        )}
                      </div>
                    </div>
                    <div>
                      <FiChevronRight className="h-5 w-5 text-gray-400" />
                    </div>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>

      {/* Message Thread */}
      {activeThread ? (
        <div className="flex-1 flex flex-col md:flex">
          <div className="flex items-center p-4 border-b border-gray-200">
            <button 
              className="md:hidden mr-2 p-1 rounded-md text-gray-500 hover:bg-gray-100"
              onClick={() => setActiveThread(null)}
            >
              <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
              </svg>
            </button>
            <div className="flex-shrink-0">
              <div className="h-10 w-10 rounded-full bg-gray-200 flex items-center justify-center">
                <FiUser className="h-5 w-5 text-gray-500" />
              </div>
            </div>
            <div className="ml-3">
              <p className="text-sm font-medium text-gray-900">
                {threads.find(t => t.id === activeThread)?.user.name}
              </p>
              <p className="text-xs text-gray-500">
                {threads.find(t => t.id === activeThread)?.property}
              </p>
            </div>
          </div>
          
          <div className="flex-1 p-4 overflow-y-auto bg-gray-50">
            <div className="space-y-4">
              {messages.map((message) => (
                <div 
                  key={message.id} 
                  className={`flex ${message.sender === 'you' ? 'justify-end' : 'justify-start'}`}
                >
                  <div 
                    className={`max-w-xs md:max-w-md lg:max-w-lg px-4 py-2 rounded-lg ${
                      message.sender === 'you' 
                        ? 'bg-blue-600 text-white rounded-br-none' 
                        : 'bg-white border border-gray-200 rounded-bl-none'
                    }`}
                  >
                    <p className="text-sm">{message.text}</p>
                    <p className={`text-xs mt-1 text-right ${
                      message.sender === 'you' ? 'text-blue-100' : 'text-gray-500'
                    }`}>
                      {message.time}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>
          
          <div className="p-4 border-t border-gray-200">
            <form onSubmit={handleSendMessage} className="flex space-x-2">
              <input
                type="text"
                className="flex-1 border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                placeholder="Type a message..."
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
              />
              <button
                type="submit"
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                Send
              </button>
            </form>
          </div>
        </div>
      ) : (
        <div className="hidden md:flex flex-1 items-center justify-center bg-gray-50">
          <div className="text-center">
            <FiMail className="mx-auto h-12 w-12 text-gray-400" />
            <h3 className="mt-2 text-sm font-medium text-gray-900">No message selected</h3>
            <p className="mt-1 text-sm text-gray-500">Select a message from the list or start a new conversation.</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Messages;
