import axios from 'axios';
import { toast } from 'react-hot-toast';

// Create axios instance with base URL
const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000/api',
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 10000, // 10 seconds
  withCredentials: true, // For CORS with credentials
});

// Request interceptor
api.interceptors.request.use(
  (config) => {
    console.log(`[API] ${config.method?.toUpperCase()} ${config.url}`, {
      data: config.data,
      params: config.params,
    });

    // Get token from localStorage
    const token = localStorage.getItem('token');
    
    // If token exists, add it to headers
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    
    return config;
  },
  (error) => {
    console.error('[API] Request error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor
api.interceptors.response.use(
  (response) => {
    console.log(`[API] Response ${response.status} ${response.config.url}`, {
      data: response.data
    });
    return response.data;
  },
  (error) => {
    const errorData = {
      message: error.message,
      response: error.response ? {
        status: error.response.status,
        statusText: error.response.statusText,
        data: error.response.data,
        headers: error.response.headers,
      } : null,
      request: error.request ? 'Request was made but no response received' : 'No request was made',
      config: {
        url: error.config?.url,
        method: error.config?.method,
        headers: error.config?.headers,
      },
    };

    console.error('[API] Response error:', errorData);

    // Only show error toast if there's a message and it's not a 401 (handled by interceptor)
    if (error.response?.status !== 401) {
      const errorMessage = error.response?.data?.message || error.message || 'An error occurred';
      toast.error(errorMessage);
    }

    return Promise.reject(error);
  }
);

// API methods
export default {
  // Auth
  auth: {
    login: (credentials) => api.post('/auth/login', credentials),
    register: (userData) => api.post('/auth/register', userData),
    me: () => api.get('/auth/me'),
    updateProfile: (userData) => api.put('/auth/me', userData),
    changePassword: (data) => api.put('/auth/change-password', data),
    forgotPassword: (email) => api.post('/auth/forgot-password', { email }),
    resetPassword: (token, data) => api.post(`/auth/reset-password/${token}`, data),
  },
  
  // Users
  users: {
    getAll: (params) => api.get('/users', { params }),
    getById: (id) => api.get(`/users/${id}`),
    create: (userData) => api.post('/users', userData),
    update: (id, userData) => api.put(`/users/${id}`, userData),
    delete: (id) => api.delete(`/users/${id}`),
  },
  
  // Properties
  properties: {
    getAll: (params) => api.get('/properties', { params }),
    getById: (id) => api.get(`/properties/${id}`),
    create: (propertyData) => {
      const formData = new FormData();
      
      // Append all fields to form data
      Object.entries(propertyData).forEach(([key, value]) => {
        if (key === 'images' && Array.isArray(value)) {
          // Handle multiple image files
          value.forEach((file, index) => {
            formData.append('images', file);
          });
        } else if (value !== null && value !== undefined) {
          formData.append(key, value);
        }
      });
      
      return api.post('/properties', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
    },
    update: (id, propertyData) => {
      const formData = new FormData();
      
      // Append all fields to form data
      Object.entries(propertyData).forEach(([key, value]) => {
        if (key === 'images' && Array.isArray(value)) {
          // Handle multiple image files
          value.forEach((file, index) => {
            if (file instanceof File) {
              formData.append('newImages', file);
            } else {
              // It's an existing image URL or ID
              formData.append('existingImages', file);
            }
          });
        } else if (value !== null && value !== undefined) {
          formData.append(key, value);
        }
      });
      
      return api.put(`/properties/${id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
    },
    delete: (id) => api.delete(`/properties/${id}`),
    search: (params) => api.get('/properties/search', { params }),
    getByUser: (userId) => api.get(`/users/${userId}/properties`),
  },
  
  // Favorites
  favorites: {
    getAll: () => api.get('/favorites'),
    add: (propertyId) => api.post('/favorites', { propertyId }),
    remove: (propertyId) => api.delete(`/favorites/${propertyId}`),
    check: (propertyId) => api.get(`/favorites/check/${propertyId}`),
  },
  
  // Messages
  messages: {
    getAll: () => api.get('/messages'),
    getConversation: (userId) => api.get(`/messages/conversation/${userId}`),
    send: (data) => api.post('/messages', data),
    markAsRead: (messageId) => api.put(`/messages/${messageId}/read`),
  },
  
  // Uploads
  uploads: {
    uploadImage: (file) => {
      const formData = new FormData();
      formData.append('image', file);
      
      return api.post('/uploads/images', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
    },
  },
};

export { api }; // Export the axios instance for custom requests
