import React, { useState, useEffect } from 'react';
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableContainer, 
  TableHead, 
  TableRow, 
  Paper, 
  IconButton, 
  TextField, 
  Button, 
  Dialog, 
  DialogActions, 
  DialogContent, 
  DialogTitle,
  Tooltip
} from '@mui/material';
import { 
  Edit as EditIcon, 
  Delete as DeleteIcon, 
  Search as SearchIcon,
  Block as BlockIcon,
  CheckCircle as CheckCircleIcon
} from '@mui/icons-material';
import api from '../../services/api';
import { toast } from 'react-toastify';

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedUser, setSelectedUser] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [actionType, setActionType] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const { data } = await api.get('/api/users');
      setUsers(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching users:', error);
      toast.error('Failed to load users');
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value.toLowerCase());
  };

  const filteredUsers = users.filter(user => 
    user.firstName?.toLowerCase().includes(searchTerm) ||
    user.lastName?.toLowerCase().includes(searchTerm) ||
    user.email?.toLowerCase().includes(searchTerm) ||
    user.phone?.toLowerCase().includes(searchTerm)
  );

  const handleActionClick = (user, type) => {
    setSelectedUser(user);
    setActionType(type);
    setOpenDialog(true);
  };

  const handleConfirmAction = async () => {
    if (!selectedUser) return;

    try {
      if (actionType === 'delete') {
        await api.delete(`/api/users/${selectedUser._id}`);
        toast.success('User deleted successfully');
      } else if (actionType === 'toggleStatus') {
        await api.put(`/api/users/${selectedUser._id}/status`, {
          isActive: !selectedUser.isActive
        });
        toast.success(`User ${!selectedUser.isActive ? 'activated' : 'deactivated'} successfully`);
      }
      
      fetchUsers();
      setOpenDialog(false);
    } catch (error) {
      console.error(`Error ${actionType === 'delete' ? 'deleting' : 'updating'} user:`, error);
      toast.error(`Failed to ${actionType === 'delete' ? 'delete' : 'update'} user`);
    }
  };

  const getStatusColor = (isActive) => {
    return isActive ? 'success.main' : 'error.main';
  };

  if (loading) {
    return <div>Loading users...</div>;
  }

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <TextField
          variant="outlined"
          size="small"
          placeholder="Search users..."
          value={searchTerm}
          onChange={handleSearch}
          InputProps={{
            startAdornment: <SearchIcon style={{ marginRight: '8px', color: '#9e9e9e' }} />,
          }}
          style={{ width: '300px' }}
        />
      </div>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Email</TableCell>
              <TableCell>Phone</TableCell>
              <TableCell>Role</TableCell>
              <TableCell>Status</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {filteredUsers.map((user) => (
              <TableRow key={user._id}>
                <TableCell>{`${user.firstName} ${user.lastName}`}</TableCell>
                <TableCell>{user.email}</TableCell>
                <TableCell>{user.phone || 'N/A'}</TableCell>
                <TableCell>{user.role}</TableCell>
                <TableCell sx={{ color: getStatusColor(user.isActive) }}>
                  {user.isActive ? 'Active' : 'Inactive'}
                </TableCell>
                <TableCell>
                  <Tooltip title="Edit User">
                    <IconButton onClick={() => handleActionClick(user, 'edit')}>
                      <EditIcon color="primary" />
                    </IconButton>
                  </Tooltip>
                  <Tooltip title={user.isActive ? 'Deactivate' : 'Activate'}>
                    <IconButton onClick={() => handleActionClick(user, 'toggleStatus')}>
                      {user.isActive ? (
                        <BlockIcon color="error" />
                      ) : (
                        <CheckCircleIcon color="success" />
                      )}
                    </IconButton>
                  </Tooltip>
                  <Tooltip title="Delete User">
                    <IconButton onClick={() => handleActionClick(user, 'delete')}>
                      <DeleteIcon color="error" />
                    </IconButton>
                  </Tooltip>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>
          {actionType === 'delete' 
            ? 'Confirm Delete' 
            : actionType === 'toggleStatus'
            ? selectedUser?.isActive ? 'Deactivate User' : 'Activate User'
            : 'Edit User'}
        </DialogTitle>
        <DialogContent>
          {actionType === 'delete' && (
            <p>Are you sure you want to delete {selectedUser?.firstName} {selectedUser?.lastName}?</p>
          )}
          {actionType === 'toggleStatus' && (
            <p>Are you sure you want to {selectedUser?.isActive ? 'deactivate' : 'activate'} this user?</p>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Cancel</Button>
          <Button 
            onClick={handleConfirmAction} 
            color={actionType === 'delete' ? 'error' : 'primary'}
            variant="contained"
          >
            {actionType === 'delete' 
              ? 'Delete' 
              : actionType === 'toggleStatus'
              ? selectedUser?.isActive ? 'Deactivate' : 'Activate'
              : 'Save'}
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default UserManagement;
