import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { ArrowLeft, MapPin, Bed, Bath, Ruler, Heart, Share2, Phone, MessageSquare } from 'lucide-react';

const PropertyDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [property, setProperty] = useState(null);
  const [loading, setLoading] = useState(true);
  const [activeImage, setActiveImage] = useState(0);
  const [favorite, setFavorite] = useState(false);

  // Mock data - replace with API call
  useEffect(() => {
    const fetchProperty = async () => {
      try {
        await new Promise(resolve => setTimeout(resolve, 500));
        
        // Mock property data
        const mockProperty = {
          id: id,
          title: 'Modern Apartment in Bole',
          price: 2500000,
          type: 'Apartment',
          bedrooms: 3,
          bathrooms: 2,
          area: 120,
          yearBuilt: 2020,
          description: 'Beautiful modern apartment located in the heart of Bole. Features include a spacious living area, modern kitchen, and balcony with city views.',
          address: 'Bole Road, Addis Ababa, Ethiopia',
          features: ['Parking', 'Security', 'Gym', 'Swimming Pool', '24/7 Electricity'],
          images: [
            'https://images.unsplash.com/photo-1560448204-e02f11c3d0e2?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80',
            'https://images.unsplash.com/photo-1580587771525-78b9dba3b914?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80',
            'https://images.unsplash.com/photo-1512917774080-9991f1c4c750?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80',
          ],
          agent: {
            name: 'John Doe',
            phone: '+251911223344',
            email: 'john.doe@example.com',
            image: 'https://randomuser.me/api/portraits/men/1.jpg'
          }
        };
        
        setProperty(mockProperty);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching property:', error);
        setLoading(false);
      }
    };

    fetchProperty();
  }, [id]);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (!property) {
    return (
      <div className="text-center py-12">
        <h2 className="text-2xl font-bold mb-4">Property not found</h2>
        <button 
          onClick={() => navigate(-1)}
          className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          Go back
        </button>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-6">
        <button 
          onClick={() => navigate(-1)}
          className="flex items-center text-blue-600 hover:text-blue-800 mb-4"
        >
          <ArrowLeft className="w-5 h-5 mr-1" /> Back to Properties
        </button>
        <h1 className="text-3xl font-bold text-gray-900">{property.title}</h1>
        <div className="flex items-center text-gray-600 mt-2">
          <MapPin className="w-4 h-4 mr-1" />
          <span>{property.address}</span>
        </div>
      </div>

      <div className="bg-white rounded-lg shadow-md overflow-hidden mb-8">
        {/* Main Image */}
        <div className="relative h-96 bg-gray-100">
          <img 
            src={property.images[activeImage]} 
            alt={property.title}
            className="w-full h-full object-cover"
          />
          <div className="absolute bottom-4 left-4 bg-blue-600 text-white px-3 py-1 rounded-full text-sm font-medium">
            ETB {property.price.toLocaleString()}
          </div>
          <button 
            onClick={() => setFavorite(!favorite)}
            className="absolute top-4 right-4 bg-white p-2 rounded-full shadow-md hover:bg-gray-100"
          >
            <Heart className={`w-6 h-6 ${favorite ? 'fill-red-500 text-red-500' : 'text-gray-600'}`} />
          </button>
        </div>

        {/* Thumbnails */}
        <div className="flex p-4 space-x-2 overflow-x-auto">
          {property.images.map((img, index) => (
            <button
              key={index}
              onClick={() => setActiveImage(index)}
              className={`flex-shrink-0 w-20 h-16 rounded overflow-hidden ${activeImage === index ? 'ring-2 ring-blue-500' : ''}`}
            >
              <img 
                src={img} 
                alt={`${property.title} - ${index + 1}`}
                className="w-full h-full object-cover"
              />
            </button>
          ))}
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Left Column - Property Details */}
        <div className="lg:col-span-2">
          <div className="bg-white rounded-lg shadow-md p-6 mb-6">
            <h2 className="text-xl font-semibold mb-4">Property Details</h2>
            <p className="text-gray-700 mb-6">{property.description}</p>
            
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
              <div className="flex flex-col items-center p-3 bg-gray-50 rounded-lg">
                <Bed className="w-6 h-6 text-blue-600 mb-1" />
                <span className="text-sm text-gray-600">{property.bedrooms} Beds</span>
              </div>
              <div className="flex flex-col items-center p-3 bg-gray-50 rounded-lg">
                <Bath className="w-6 h-6 text-blue-600 mb-1" />
                <span className="text-sm text-gray-600">{property.bathrooms} Baths</span>
              </div>
              <div className="flex flex-col items-center p-3 bg-gray-50 rounded-lg">
                <Ruler className="w-6 h-6 text-blue-600 mb-1" />
                <span className="text-sm text-gray-600">{property.area} m²</span>
              </div>
              <div className="flex flex-col items-center p-3 bg-gray-50 rounded-lg">
                <span className="text-blue-600 font-medium mb-1">Year</span>
                <span className="text-sm text-gray-600">{property.yearBuilt}</span>
              </div>
            </div>

            <div>
              <h3 className="font-medium text-gray-900 mb-2">Features</h3>
              <div className="flex flex-wrap gap-2">
                {property.features.map((feature, index) => (
                  <span key={index} className="bg-blue-50 text-blue-700 text-sm px-3 py-1 rounded-full">
                    {feature}
                  </span>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Right Column - Contact Agent */}
        <div>
          <div className="bg-white rounded-lg shadow-md p-6 sticky top-6">
            <h2 className="text-xl font-semibold mb-4">Contact Agent</h2>
            
            <div className="flex items-center mb-6">
              <img 
                src={property.agent.image} 
                alt={property.agent.name}
                className="w-16 h-16 rounded-full object-cover mr-4"
              />
              <div>
                <h3 className="font-medium text-gray-900">{property.agent.name}</h3>
                <p className="text-sm text-gray-600">Real Estate Agent</p>
              </div>
            </div>

            <div className="space-y-4">
              <a 
                href={`tel:${property.agent.phone}`}
                className="flex items-center justify-center w-full px-4 py-3 bg-green-600 text-white rounded-md hover:bg-green-700 transition-colors"
              >
                <Phone className="w-5 h-5 mr-2" />
                Call Now
              </a>
              <button className="flex items-center justify-center w-full px-4 py-3 border border-blue-600 text-blue-600 rounded-md hover:bg-blue-50 transition-colors">
                <MessageSquare className="w-5 h-5 mr-2" />
                Send Message
              </button>
            </div>

            <div className="mt-6 pt-6 border-t border-gray-200">
              <h3 className="font-medium text-gray-900 mb-3">Share this property</h3>
              <div className="flex space-x-3">
                <button className="p-2 rounded-full bg-blue-100 text-blue-600 hover:bg-blue-200">
                  <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M24 4.557c-.883.392-1.832.656-2.828.775 1.017-.609 1.798-1.574 2.165-2.724-.951.564-2.005.974-3.127 1.195-.897-.957-2.178-1.555-3.594-1.555-3.179 0-5.515 2.966-4.797 6.045-4.091-.205-7.719-2.165-10.148-5.144-1.29 2.213-.669 5.108 1.523 6.574-.806-.026-1.566-.247-2.229-.616-.054 2.281 1.581 4.415 3.949 4.89-.693.188-1.452.232-2.224.084.626 1.956 2.444 3.379 4.6 3.419-2.07 1.623-4.678 2.348-7.29 2.04 2.179 1.397 4.768 2.212 7.548 2.212 9.142 0 14.307-7.721 13.995-14.646.962-.695 1.797-1.562 2.457-2.549z" />
                  </svg>
                </button>
                <button className="p-2 rounded-full bg-blue-600 text-white hover:bg-blue-700">
                  <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M9 8h-3v4h3v12h5v-12h3.642l.358-4h-4v-1.667c0-.955.192-1.333 1.115-1.333h2.885v-5h-3.808c-3.596 0-5.192 1.583-5.192 4.615v3.385z" />
                  </svg>
                </button>
                <button className="p-2 rounded-full bg-gray-100 text-gray-600 hover:bg-gray-200">
                  <Share2 className="w-5 h-5" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PropertyDetails;
