import React, { useState, useRef } from 'react';
import { Outlet, Link, useLocation, useNavigate, useMatch } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { 
  FiHome, 
  FiUser, 
  FiPlusSquare, 
  FiList, 
  FiMessageSquare, 
  FiHeart, 
  FiSettings,
  FiChevronDown,
  FiLogOut,
  FiUserCheck,
  FiShield,
  FiSend
} from 'react-icons/fi';

const Dashboard = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);
  const [message, setMessage] = useState('');
  const isMessagesRoute = location.pathname === '/dashboard/messages';
  const isAdmin = user?.role === 'admin';
  const isAdminRoute = location.pathname.includes('/dashboard/admin');
  const messageInputRef = useRef(null);

  const menuItems = [
    { icon: FiHome, label: 'Overview', path: '/dashboard' },
    { icon: FiList, label: 'My Listings', path: '/dashboard/my-listings' },
    { icon: FiPlusSquare, label: 'Add Property', path: '/dashboard/create-property' },
    { icon: FiMessageSquare, label: 'Messages', path: '/dashboard/messages' },
    { icon: FiHeart, label: 'Favorites', path: '/dashboard/favorites' },
    { icon: FiHeart, label: 'My Interests', path: '/dashboard/interests' },
    { icon: FiSettings, label: 'Settings', path: '/dashboard/settings' },
  ];

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!message.trim()) return;
    
    try {
      // TODO: Implement message sending logic
      console.log('Sending message:', message);
      setMessage('');
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage(e);
    }
  };

  const toggleUserMenu = () => {
    setIsUserMenuOpen(!isUserMenuOpen);
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const toggleAdminView = () => {
    if (isAdminRoute) {
      navigate('/dashboard');
    } else {
      navigate('/dashboard/admin');
    }
    setIsUserMenuOpen(false);
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="hidden md:flex md:flex-shrink-0">
        <div className="flex flex-col w-64 bg-white border-r border-gray-200">
          <div className="flex items-center justify-center h-16 px-4 border-b border-gray-200">
            <h1 className="text-xl font-bold text-blue-600">Ethiopian Broker</h1>
          </div>
          
          {/* Navigation */}
          <nav className="flex-1 px-2 py-4 space-y-1 overflow-y-auto">
            {menuItems.map((item) => {
              const Icon = item.icon;
              const isActive = location.pathname === item.path;
              return (
                <Link
                  key={item.path}
                  to={item.path}
                  className={`flex items-center px-4 py-3 text-sm font-medium rounded-md ${
                    isActive
                      ? 'bg-blue-50 text-blue-600'
                      : 'text-gray-700 hover:bg-gray-100'
                  }`}
                >
                  <Icon className={`w-5 h-5 mr-3 ${isActive ? 'text-blue-500' : 'text-gray-400'}`} />
                  {item.label}
                </Link>
              );
            })}
          </nav>
        </div>
      </div>

      {/* Main content */}
      <div className="flex flex-col flex-1 overflow-hidden">
        {/* Top Navigation */}
        <header className="bg-white shadow-sm">
          <div className="flex items-center justify-between px-4 py-3">
            <h1 className="text-lg font-semibold text-gray-900">
              {isAdminRoute ? 'Admin Dashboard' : 'My Dashboard'}
            </h1>
            
            <div className="flex items-center space-x-4">
              {/* Admin Toggle Button (only show if user is admin) */}
              {isAdmin && (
                <button
                  onClick={toggleAdminView}
                  className={`px-3 py-1.5 rounded-md text-sm font-medium flex items-center ${
                    isAdminRoute 
                      ? 'bg-purple-100 text-purple-700' 
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  <FiShield className="mr-2" />
                  {isAdminRoute ? 'User View' : 'Admin View'}
                </button>
              )}
              
              {/* User Menu */}
              <div className="relative">
                <button
                  type="button"
                  className="flex items-center max-w-xs rounded-full focus:outline-none"
                  id="user-menu"
                  aria-expanded="false"
                  onClick={toggleUserMenu}
                >
                  <span className="sr-only">Open user menu</span>
                  <div className="h-8 w-8 rounded-full bg-blue-500 flex items-center justify-center text-white">
                    {user?.firstName?.charAt(0) || 'U'}
                  </div>
                  <FiChevronDown className="ml-1 text-gray-400" />
                </button>
                
                {/* Dropdown menu */}
                {isUserMenuOpen && (
                  <div className="origin-top-right absolute right-0 mt-2 w-56 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 focus:outline-none z-10">
                    <div className="py-1" role="none">
                      <div className="px-4 py-2 text-sm text-gray-700 border-b">
                        <p className="font-medium">{user?.firstName} {user?.lastName}</p>
                        <p className="text-gray-500 text-xs">{user?.email}</p>
                      </div>
                      
                      
                      
                      <Link
                        to="/dashboard/settings"
                        className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 flex items-center"
                        onClick={() => setIsUserMenuOpen(false)}
                      >
                        <FiSettings className="mr-2" />
                        Settings
                      </Link>
                      
                      <button
                        onClick={handleLogout}
                        className="w-full text-left px-4 py-2 text-sm text-red-600 hover:bg-gray-100 flex items-center border-t mt-1"
                      >
                        <FiLogOut className="mr-2" />
                        Sign out
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </header>

        {/* Page content */}
        <div className="flex-1 flex flex-col overflow-hidden">
          <main className="flex-1 overflow-y-auto p-4 md:p-6">
            <Outlet />
          </main>
          
          {/* Message input (only shown on messages route) */}
          {isMessagesRoute && (
            <div className="border-t border-gray-200 bg-white p-4">
              <form onSubmit={handleSendMessage} className="flex items-center">
                <input
                  type="text"
                  ref={messageInputRef}
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  onKeyDown={handleKeyDown}
                  placeholder="Type a message..."
                  className="flex-1 border border-gray-300 rounded-full px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
                <button
                  type="submit"
                  disabled={!message.trim()}
                  className={`ml-2 p-2 rounded-full ${
                    message.trim() 
                      ? 'bg-blue-500 text-white hover:bg-blue-600' 
                      : 'bg-gray-200 text-gray-400 cursor-not-allowed'
                  }`}
                >
                  <FiSend className="h-5 w-5" />
                </button>
              </form>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
