import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  FiHome, 
  FiFileText, 
  FiSettings, 
  FiInfo, 
  FiHeart,
  FiClock,
  FiMessageSquare,
  FiEye,
  FiStar,
  FiList,
  FiRefreshCw
} from 'react-icons/fi';
import { formatDistanceToNow } from 'date-fns';
import api from '../../services/api';

// Reusable QuickAction component
const QuickAction = ({ title, description, icon: Icon, path, onClick, color }) => {
  const className = `flex items-start p-4 space-x-4 bg-white rounded-lg shadow hover:shadow-md transition-all duration-200 ${
    onClick ? 'cursor-pointer' : ''
  }`;
  
  const content = (
    <>
      <div className={`p-3 rounded-full ${color}`}>
        <Icon className="w-6 h-6" />
      </div>
      <div>
        <h4 className="font-medium text-gray-900">{title}</h4>
        <p className="text-sm text-gray-500">{description}</p>
      </div>
    </>
  );

  return path ? (
    <Link to={path} className={className}>
      {content}
    </Link>
  ) : (
    <button onClick={onClick} className={`w-full text-left ${className}`}>
      {content}
    </button>
  );
};

// Reusable InterestCard component
const InterestCard = ({ interest, onRefresh }) => {
  return (
    <div className="overflow-hidden bg-white rounded-lg shadow">
      {interest.property?.images?.[0] && (
        <div className="h-48 overflow-hidden">
          <img 
            src={interest.property.images[0]} 
            alt={interest.property.title}
            className="object-cover w-full h-full"
          />
        </div>
      )}
      <div className="p-4">
        <div className="flex items-start justify-between">
          <h4 className="text-lg font-medium text-gray-900 line-clamp-1">
            {interest.property?.title || 'Property'}
          </h4>
          <span className={`px-2 py-1 text-xs font-medium rounded-full ${
            interest.status === 'pending' ? 'bg-yellow-100 text-yellow-800' :
            interest.status === 'approved' ? 'bg-green-100 text-green-800' :
            interest.status === 'rejected' ? 'bg-red-100 text-red-800' :
            'bg-gray-100 text-gray-800'
          }`}>
            {interest.status || 'Pending'}
          </span>
        </div>
        <p className="mt-2 text-sm text-gray-600 line-clamp-2">
          {interest.message || 'No additional message provided'}
        </p>
        <div className="flex items-center justify-between mt-4 text-sm text-gray-500">
          <span>Submitted {formatDistanceToNow(new Date(interest.createdAt))} ago</span>
          <Link 
            to={`/properties/${interest.property?._id}`}
            className="font-medium text-blue-600 hover:text-blue-500"
          >
            View Property
          </Link>
        </div>
      </div>
    </div>
  );
};

// Reusable ActivityItem component
const ActivityItem = ({ activity }) => {
  const getActivityIcon = () => {
    switch (activity.type) {
      case 'message': return <FiMessageSquare className="text-blue-500" />;
      case 'view': return <FiEye className="text-green-500" />;
      case 'favorite': return <FiStar className="text-yellow-500" />;
      case 'interest': return <FiHeart className="text-pink-500" />;
      default: return <FiInfo className="text-gray-500" />;
    }
  };

  return (
    <li className="px-6 py-4">
      <div className="flex items-center">
        <div className="flex-shrink-0">
          {getActivityIcon()}
        </div>
        <div className="ml-4 flex-1">
          <div className="flex items-center justify-between">
            <p className="text-sm font-medium text-gray-900">
              {activity.message}
            </p>
            <div className="text-sm text-gray-500">
              <FiClock className="inline mr-1" />
              {formatDistanceToNow(new Date(activity.createdAt), { addSuffix: true })}
            </div>
          </div>
          {activity.metadata?.details && (
            <div className="mt-1 text-sm text-gray-500">
              {activity.metadata.details}
            </div>
          )}
        </div>
      </div>
    </li>
  );
};

const Overview = () => {
  const [recentActivity, setRecentActivity] = useState([]);
  const [userInterests, setUserInterests] = useState([]);
  const [loading, setLoading] = useState({ interests: true, activities: true });
  const [activeTab, setActiveTab] = useState('recent');
  const [showAllInterests, setShowAllInterests] = useState(false);

  const fetchData = async () => {
    try {
      setLoading(prev => ({ ...prev, interests: true, activities: !showAllInterests }));
      
      // Fetch user's interests
      const interestsResponse = await api.interests.getMine({ 
        limit: 100,
        populate: 'property'
      });
      setUserInterests(interestsResponse.data || []);
      setLoading(prev => ({ ...prev, interests: false }));

      // Fetch recent activities if not showing all interests
      if (!showAllInterests) {
        const activitiesResponse = await api.users.getActivities();
        setRecentActivity(activitiesResponse.data || []);
        setLoading(prev => ({ ...prev, activities: false }));
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      setLoading({ interests: false, activities: false });
    }
  };

  useEffect(() => {
    fetchData();
  }, [showAllInterests]);

  const quickActions = [
    { 
      title: 'Post a Property', 
      description: 'List a new property for sale or rent',
      icon: FiHome,
      path: '/dashboard/create-property',
      color: 'bg-blue-100 text-blue-600'
    },
    { 
      title: 'View My Listings', 
      description: 'Manage your property listings',
      icon: FiFileText,
      path: '/dashboard/my-listings',
      color: 'bg-green-100 text-green-600'
    },
    { 
      title: showAllInterests ? 'Back to Overview' : 'My Interests', 
      description: showAllInterests ? 'Return to dashboard' : 'View all your saved interests',
      icon: showAllInterests ? FiHome : FiHeart,
      onClick: () => setShowAllInterests(!showAllInterests),
      color: showAllInterests ? 'bg-gray-100 text-gray-600' : 'bg-pink-100 text-pink-600'
    },
    { 
      title: 'Account Settings', 
      description: 'Update your profile and preferences',
      icon: FiSettings,
      path: '/dashboard/settings',
      color: 'bg-purple-100 text-purple-600'
    }
  ];

  const renderEmptyState = (icon, title, description, showButton = true) => (
    <div className="p-8 text-center bg-white rounded-lg shadow">
      {icon}
      <h3 className="mt-2 text-lg font-medium text-gray-900">{title}</h3>
      <p className="mt-1 text-gray-500">{description}</p>
      {showButton && (
        <div className="mt-6">
          <Link
            to="/properties"
            className="inline-flex items-center px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md shadow-sm hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
          >
            Browse Properties
          </Link>
        </div>
      )}
    </div>
  );

  const renderLoadingState = (message) => (
    <div className="flex items-center justify-center p-8">
      <div className="w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full animate-spin"></div>
      <span className="ml-2 text-gray-600">{message}</span>
    </div>
  );

  const renderRecentActivities = () => (
    <div className="bg-white shadow overflow-hidden sm:rounded-md">
      <ul className="divide-y divide-gray-200">
        {loading.activities ? (
          <li>{renderLoadingState('Loading activities...')}</li>
        ) : recentActivity.length > 0 ? (
          recentActivity.map(activity => (
            <ActivityItem key={activity._id} activity={activity} />
          ))
        ) : (
          <li>{renderEmptyState(
            <FiInfo className="w-12 h-12 mx-auto text-gray-400" />,
            'No recent activities',
            'Your recent activities will appear here',
            false
          )}</li>
        )}
      </ul>
    </div>
  );

  const renderInterestsList = () => (
    <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3">
      {userInterests.map(interest => (
        <InterestCard key={interest._id} interest={interest} onRefresh={fetchData} />
      ))}
    </div>
  );

  const renderMainContent = () => {
    if (showAllInterests) {
      return (
        <>
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-medium text-gray-900">
              My Saved Interests ({userInterests.length})
            </h3>
            <button
              onClick={fetchData}
              className="p-2 text-gray-500 rounded-full hover:bg-gray-100"
              title="Refresh"
            >
              <FiRefreshCw className={`w-5 h-5 ${loading.interests ? 'animate-spin' : ''}`} />
            </button>
          </div>
          
          {loading.interests ? (
            renderLoadingState('Loading your interests...')
          ) : userInterests.length > 0 ? (
            renderInterestsList()
          ) : (
            renderEmptyState(
              <FiHeart className="w-12 h-12 mx-auto text-gray-400" />,
              'No interests yet',
              'Properties you\'re interested in will appear here.'
            )
          )}
        </>
      );
    }

    return (
      <div className="mt-8">
        <div className="border-b border-gray-200">
          <nav className="flex -mb-px space-x-8">
            <button
              onClick={() => setActiveTab('recent')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'recent' 
                  ? 'border-blue-500 text-blue-600' 
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              Recent Activity
            </button>
            <button
              onClick={() => setActiveTab('mylist')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'mylist' 
                  ? 'border-blue-500 text-blue-600' 
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              My List ({userInterests.length})
            </button>
          </nav>
        </div>

        <div className="mt-4">
          {activeTab === 'recent' ? (
            renderRecentActivities()
          ) : (
            <div className="bg-white shadow overflow-hidden sm:rounded-md">
              {loading.interests ? (
                renderLoadingState('Loading your interests...')
              ) : userInterests.length > 0 ? (
                <ul className="divide-y divide-gray-200">
                  {userInterests.slice(0, 5).map(interest => (
                    <li key={interest._id} className="px-6 py-4">
                      <div className="flex items-center">
                        {interest.property?.images?.[0] && (
                          <div className="flex-shrink-0 h-16 w-16">
                            <img
                              className="h-16 w-16 rounded-md object-cover"
                              src={interest.property.images[0]}
                              alt={interest.property.title}
                            />
                          </div>
                        )}
                        <div className="ml-4 flex-1">
                          <div className="flex items-center justify-between">
                            <h4 className="text-sm font-medium text-gray-900">
                              {interest.property?.title || 'Property'}
                            </h4>
                            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                              {interest.status || 'Pending'}
                            </span>
                          </div>
                          <div className="mt-1 text-sm text-gray-500">
                            {interest.message && <p className="truncate">{interest.message}</p>}
                            <p className="text-xs text-gray-400 mt-1">
                              Submitted {formatDistanceToNow(new Date(interest.createdAt))} ago
                            </p>
                          </div>
                        </div>
                      </div>
                    </li>
                  ))}
                </ul>
              ) : (
                renderEmptyState(
                  <FiHeart className="w-12 h-12 mx-auto text-gray-400" />,
                  'No interests yet',
                  'Properties you\'re interested in will appear here.'
                )
              )}
            </div>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold text-gray-900">
          {showAllInterests ? 'My Saved Interests' : 'Dashboard Overview'}
        </h2>
      </div>

      {/* Quick Actions */}
      <div className="mt-8">
        <h3 className="text-lg font-medium text-gray-900">Quick Actions</h3>
        <div className="grid grid-cols-1 gap-6 mt-4 sm:grid-cols-2 lg:grid-cols-4">
          {quickActions.map((action, index) => (
            <QuickAction key={index} {...action} />
          ))}
        </div>
      </div>

      {/* Main Content */}
      <div className="mt-8">
        {renderMainContent()}
      </div>
    </div>
  );
};

export default Overview;
