import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ArrowLeft, MapPin, Bed, Bath, RulerSquare, Heart, Share2, Phone, Mail } from 'lucide-react';
import { toast } from 'react-hot-toast';

// Mock data - replace with API call in production
const mockProperty = {
  id: 1,
  title: 'Modern Apartment in Bole',
  address: 'Bole Road, Addis Ababa',
  price: 25000,
  type: 'Apartment',
  status: 'For Rent',
  beds: 3,
  baths: 2,
  area: 180,
  yearBuilt: 2020,
  description: 'This stunning modern apartment is located in the heart of Bole, Addis Ababa. It features spacious rooms, high-end finishes, and breathtaking city views.',
  images: [
    'https://images.unsplash.com/photo-1560448204-e02f11c3d0e2?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80',
    'https://images.unsplash.com/photo-1580587771525-78b9dba3b914?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80',
  ],
  agent: {
    name: 'Alemayehu Kebede',
    phone: '+251 911 123 456',
    email: 'alemu@ethiopianbroker.com',
    image: 'https://randomuser.me/api/portraits/men/32.jpg'
  }
};

const PropertyDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [property, setProperty] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isSaved, setIsSaved] = useState(false);

  // Fetch property details
  useEffect(() => {
    const fetchProperty = async () => {
      try {
        await new Promise(resolve => setTimeout(resolve, 500));
        setProperty(mockProperty);
      } catch (error) {
        toast.error('Failed to load property');
      } finally {
        setLoading(false);
      }
    };

    fetchProperty();
  }, [id]);

  const handlePrevImage = () => {
    setCurrentImageIndex(prev => 
      prev === 0 ? property.images.length - 1 : prev - 1
    );
  };

  const handleNextImage = () => {
    setCurrentImageIndex(prev => 
      prev === property.images.length - 1 ? 0 : prev + 1
    );
  };

  const handleSaveProperty = () => {
    setIsSaved(!isSaved);
    toast.success(!isSaved ? 'Saved to favorites' : 'Removed from favorites');
  };

  const handleShareProperty = async () => {
    try {
      await navigator.share({
        title: property.title,
        text: `Check out this ${property.type} in ${property.address}`,
        url: window.location.href,
      });
    } catch (error) {
      await navigator.clipboard.writeText(window.location.href);
      toast.success('Link copied to clipboard!');
    }
  };

  if (loading || !property) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <div className="flex items-center">
            <button 
              onClick={() => navigate(-1)}
              className="p-2 rounded-full hover:bg-gray-100 mr-4"
            >
              <ArrowLeft className="h-5 w-5 text-gray-600" />
            </button>
            <h1 className="text-xl font-semibold text-gray-900">
              {property.title}
            </h1>
            <div className="ml-auto flex space-x-2">
              <button 
                onClick={handleSaveProperty}
                className={`p-2 rounded-full ${isSaved ? 'text-red-500' : 'text-gray-400 hover:text-gray-600'}`}
              >
                <Heart className="h-5 w-5" fill={isSaved ? 'currentColor' : 'none'} />
              </button>
              <button 
                onClick={handleShareProperty}
                className="p-2 rounded-full text-gray-400 hover:text-gray-600"
              >
                <Share2 className="h-5 w-5" />
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main content */}
          <div className="lg:col-span-2">
            {/* Image gallery */}
            <div className="relative bg-gray-100 rounded-xl overflow-hidden mb-6" style={{ paddingBottom: '60%' }}>
              <img
                src={property.images[currentImageIndex]}
                alt={property.title}
                className="absolute inset-0 w-full h-full object-cover"
              />
              
              {property.images.length > 1 && (
                <>
                  <button 
                    onClick={handlePrevImage}
                    className="absolute left-2 top-1/2 transform -translate-y-1/2 bg-white bg-opacity-80 rounded-full p-2 shadow-md hover:bg-opacity-100"
                  >
                    <svg className="h-6 w-6 text-gray-700" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                    </svg>
                  </button>
                  <button 
                    onClick={handleNextImage}
                    className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-white bg-opacity-80 rounded-full p-2 shadow-md hover:bg-opacity-100"
                  >
                    <svg className="h-6 w-6 text-gray-700" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                    </svg>
                  </button>
                </>
              )}
            </div>

            {/* Property info */}
            <div className="bg-white rounded-xl shadow-sm p-6 mb-6">
              <div className="flex flex-col sm:flex-row sm:items-center justify-between mb-4">
                <div>
                  <h2 className="text-2xl font-bold text-gray-900">{property.title}</h2>
                  <div className="flex items-center text-gray-600 mt-1">
                    <MapPin className="h-4 w-4 mr-1" />
                    <span>{property.address}</span>
                  </div>
                </div>
                <div className="mt-3 sm:mt-0">
                  <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-blue-100 text-blue-800">
                    {property.status}
                  </span>
                </div>
              </div>

              <div className="flex flex-wrap gap-4 mb-6">
                <div className="flex items-center text-gray-700">
                  <Bed className="h-5 w-5 text-gray-500 mr-2" />
                  <span>{property.beds} {property.beds === 1 ? 'Bed' : 'Beds'}</span>
                </div>
                <div className="flex items-center text-gray-700">
                  <Bath className="h-5 w-5 text-gray-500 mr-2" />
                  <span>{property.baths} {property.baths === 1 ? 'Bath' : 'Baths'}</span>
                </div>
                <div className="flex items-center text-gray-700">
                  <RulerSquare className="h-5 w-5 text-gray-500 mr-2" />
                  <span>{property.area} m²</span>
                </div>
              </div>

              <div className="border-t border-gray-200 pt-6">
                <h3 className="text-lg font-semibold mb-3">About This Property</h3>
                <p className="text-gray-600 leading-relaxed">
                  {property.description}
                </p>
              </div>
            </div>
          </div>

          {/* Agent info */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-xl shadow-sm p-6 sticky top-6">
              <h3 className="text-lg font-semibold mb-4">Contact Agent</h3>
              <div className="flex items-center mb-4">
                <img 
                  src={property.agent.image} 
                  alt={property.agent.name} 
                  className="h-16 w-16 rounded-full object-cover mr-4"
                />
                <div>
                  <h4 className="font-medium text-gray-900">{property.agent.name}</h4>
                  <p className="text-sm text-gray-500">Real Estate Agent</p>
                </div>
              </div>
              <div className="space-y-4">
                <a 
                  href={`tel:${property.agent.phone}`}
                  className="flex items-center justify-center px-4 py-2 border border-gray-300 rounded-md text-gray-700 bg-white hover:bg-gray-50"
                >
                  <Phone className="h-5 w-5 mr-2 text-blue-600" />
                  Call Agent
                </a>
                <a 
                  href={`mailto:${property.agent.email}`}
                  className="flex items-center justify-center px-4 py-2 border border-transparent rounded-md text-white bg-blue-600 hover:bg-blue-700"
                >
                  <Mail className="h-5 w-5 mr-2" />
                  Email Agent
                </a>
              </div>
              <div className="mt-6 pt-6 border-t border-gray-200">
                <h4 className="text-sm font-medium text-gray-500 mb-2">Price</h4>
                <p className="text-2xl font-bold text-gray-900">ETB {property.price.toLocaleString()}</p>
                <p className="text-sm text-gray-500 mt-1">{property.type} • {property.status}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PropertyDetails;
