import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { toast } from 'react-hot-toast';
import { FiUpload, FiX } from 'react-icons/fi';
import api from '../../services/api';

const InterestForm = ({ onSuccess }) => {
  const { register, handleSubmit, watch, formState: { errors }, reset } = useForm({
    defaultValues: { type: 'house', transactionType: 'buy' }
  });

  const [files, setFiles] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const selectedType = watch('type');

  // Field configurations for each interest type
  const fieldConfigs = {
    house: [
      { name: 'bedrooms', label: 'Bedrooms', type: 'number', required: true },
      { name: 'bathrooms', label: 'Bathrooms', type: 'number', step: '0.5', required: true },
      { name: 'area', label: 'Area (sq ft)', type: 'number', required: true },
      { name: 'address', label: 'Address', type: 'text', required: true },
    ],
    car: [
      { name: 'make', label: 'Make', type: 'text', required: true },
      { name: 'model', label: 'Model', type: 'text', required: true },
      { name: 'year', label: 'Year', type: 'number', required: true },
      { name: 'mileage', label: 'Mileage', type: 'number', required: true },
    ],
    other: [
      { name: 'title', label: 'Title', type: 'text', required: true },
      { name: 'description', label: 'Description', type: 'textarea', required: true },
    ]
  };

  const handleFileChange = (e) => {
    const newFiles = Array.from(e.target.files);
    setFiles(prev => [...prev, ...newFiles].slice(0, 5));
  };

  const removeFile = (index) => {
    setFiles(prev => prev.filter((_, i) => i !== index));
  };

  const onSubmit = async (data) => {
    if (files.length === 0) {
      toast.error('Please upload at least one image');
      return;
    }

    setIsSubmitting(true);
    const formData = new FormData();
    
    formData.append('type', data.type);
    formData.append('transactionType', data.transactionType);
    formData.append('notes', data.notes || '');
    formData.append('details', JSON.stringify(data.details || {}));
    
    files.forEach(file => formData.append('images', file));

    try {
      await api.interests.submit(formData);
      toast.success('Interest submitted!');
      reset();
      setFiles([]);
      onSuccess?.();
    } catch (error) {
      console.error('Error:', error);
      toast.error(error.response?.data?.message || 'Submission failed');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow">
      <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Interest Type <span className="text-red-500">*</span>
            </label>
            <select
              {...register('type')}
              className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500"
              disabled={isSubmitting}
            >
              <option value="house">House</option>
              <option value="car">Car</option>
              <option value="other">Other</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Transaction <span className="text-red-500">*</span>
            </label>
            <select
              {...register('transactionType')}
              className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500"
              disabled={isSubmitting}
            >
              <option value="buy">Buy</option>
              <option value="lessor">Rent</option>
              <option value="lesse">Lessor</option>
              <option value="sell">Sell</option>
            </select>
          </div>
        </div>

        {/* Price Range */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Minimum Price ($) <span className="text-red-500">*</span>
            </label>
            <input
              type="number"
              step="0.01"
              min="0"
              {...register('details.minPrice', { 
                required: 'Minimum price is required',
                valueAsNumber: true
              })}
              className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500"
              disabled={isSubmitting}
              placeholder="e.g. 100000"
            />
            {errors.details?.minPrice && (
              <p className="mt-1 text-sm text-red-600">
                {errors.details.minPrice.message}
              </p>
            )}
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Maximum Price ($) <span className="text-red-500">*</span>
            </label>
            <input
              type="number"
              step="0.01"
              min="0"
              {...register('details.maxPrice', { 
                required: 'Maximum price is required',
                valueAsNumber: true,
                validate: (value, formValues) => 
                  Number(value) >= Number(formValues.details?.minPrice || 0) || 
                  'Must be greater than or equal to minimum price'
              })}
              className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500"
              disabled={isSubmitting}
              placeholder="e.g. 500000"
            />
            {errors.details?.maxPrice && (
              <p className="mt-1 text-sm text-red-600">
                {errors.details.maxPrice.message}
              </p>
            )}
          </div>
        </div>

        <div className="space-y-4">
          {fieldConfigs[selectedType]?.map((field) => (
            <div key={field.name}>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                {field.label}
                {field.required && <span className="text-red-500"> *</span>}
              </label>
              {field.type === 'textarea' ? (
                <textarea
                  {...register(`details.${field.name}`, { required: field.required })}
                  rows={3}
                  className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500"
                  disabled={isSubmitting}
                />
              ) : (
                <input
                  type={field.type}
                  step={field.step}
                  {...register(`details.${field.name}`, { 
                    required: field.required,
                    valueAsNumber: field.type === 'number'
                  })}
                  className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500"
                  disabled={isSubmitting}
                />
              )}
            </div>
          ))}
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Images <span className="text-red-500">*</span>
          </label>
          <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md">
            <div className="space-y-1 text-center">
              <FiUpload className="mx-auto h-12 w-12 text-gray-400" />
              <div className="flex text-sm text-gray-600">
                <label className="relative cursor-pointer bg-white rounded-md font-medium text-blue-600 hover:text-blue-500">
                  <span>Upload files</span>
                  <input
                    type="file"
                    className="sr-only"
                    multiple
                    accept="image/*"
                    onChange={handleFileChange}
                    disabled={isSubmitting}
                  />
                </label>
                <p className="pl-1">or drag and drop</p>
              </div>
              <p className="text-xs text-gray-500">PNG, JPG, WEBP up to 5MB</p>
            </div>
          </div>
          
          {files.length > 0 && (
            <div className="mt-4 grid grid-cols-2 gap-2">
              {files.map((file, index) => (
                <div key={index} className="relative group">
                  <img
                    src={URL.createObjectURL(file)}
                    alt={`Preview ${index + 1}`}
                    className="h-24 w-full object-cover rounded border"
                  />
                  <button
                    type="button"
                    onClick={() => removeFile(index)}
                    className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full p-1 opacity-0 group-hover:opacity-100"
                  >
                    <FiX className="h-4 w-4" />
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Additional Notes
          </label>
          <textarea
            {...register('notes')}
            rows={3}
            className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500"
            disabled={isSubmitting}
          />
        </div>

        <div>
          <button
            type="submit"
            disabled={isSubmitting}
            className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
          >
            {isSubmitting ? 'Submitting...' : 'Submit Interest'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default InterestForm;
