import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { Tabs, Tab, Box } from '@mui/material';
import { FiUsers, FiMessageSquare, FiFileText } from 'react-icons/fi';
import UserManagement from './UserManagement';
import AdminMessages from './AdminMessages';
import UserPosts from './UserPosts';
import LoadingSpinner from '../../components/common/LoadingSpinner';

// Tab Panel Component
function TabPanel(props) {
  const { children, value, index, ...other } = props;
  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`admin-tabpanel-${index}`}
      aria-labelledby={`admin-tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
    </div>
  );
}

const AdminDashboard = () => {
  const [tabValue, setTabValue] = useState(0);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    // Redirect if not admin
    if (user?.role !== 'admin') {
      navigate('/');
    } else {
      setLoading(false);
    }
  }, [user, navigate]);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  return (
    <Box sx={{ width: '100%', minHeight: 'calc(100vh - 64px)' }}>
      <Box sx={{ borderBottom: 1, borderColor: 'divider', bgcolor: 'background.paper' }}>
        <Tabs 
          value={tabValue} 
          onChange={handleTabChange}
          variant="scrollable"
          scrollButtons="auto"
          aria-label="admin dashboard tabs"
        >
          <Tab 
            icon={<FiUsers size={20} style={{ marginBottom: 4 }} />} 
            label="Users"
            iconPosition="start"
          />
          <Tab 
            icon={<FiMessageSquare size={20} style={{ marginBottom: 4 }} />} 
            label="Messages"
            iconPosition="start"
          />
          <Tab 
            icon={<FiFileText size={20} style={{ marginBottom: 4 }} />} 
            label="User Posts"
            iconPosition="start"
          />
        </Tabs>
      </Box>

      <TabPanel value={tabValue} index={0}>
        <UserManagement />
      </TabPanel>
      <TabPanel value={tabValue} index={1}>
        <AdminMessages />
      </TabPanel>
      <TabPanel value={tabValue} index={2}>
        <UserPosts />
      </TabPanel>
    </Box>
  );
};

export default AdminDashboard;
