        }
      });
      
      return api.post('/properties', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
    },
    update: (id, propertyData) => {
      const formData = new FormData();
      
      // Append all fields to form data
      Object.entries(propertyData).forEach(([key, value]) => {
        if (key === 'images' && Array.isArray(value)) {
          // Handle multiple image files
          value.forEach((file, index) => {
            if (file instanceof File) {
              formData.append('newImages', file);
            } else {
              // It's an existing image URL or ID
              formData.append('existingImages', file);
            }
          });
        } else if (value !== null && value !== undefined) {
          formData.append(key, value);
        }
      });
      
      return api.put(`/properties/${id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
    },
    delete: (id) => api.delete(`/properties/${id}`),
    search: (params) => api.get('/properties/search', { params }),
    getByUser: (userId) => api.get(`/users/${userId}/properties`),
  },
  
  // Favorites
  favorites: {
    getAll: () => api.get('/favorites'),
    add: (propertyId) => api.post('/favorites', { propertyId }),
    remove: (propertyId) => api.delete(`/favorites/${propertyId}`),
    check: (propertyId) => api.get(`/favorites/check/${propertyId}`),
  },
  
  // Messages
  messages: {
    getAll: () => api.get('/messages'),
    getConversation: (userId) => api.get(`/messages/conversation/${userId}`),
    send: (data) => api.post('/messages', data),
    markAsRead: (messageId) => api.put(`/messages/${messageId}/read`),
  },
  
  // Uploads
  uploads: {
    uploadImage: (file) => {
      const formData = new FormData();
      formData.append('image', file);
      
      return api.post('/uploads/images', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
    },
  },
