import { createContext, useState, useContext, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { toast } from 'react-hot-toast';
import { api } from '../services/api';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const location = useLocation();

  // Check if user is logged in on initial load
  useEffect(() => {
    const loadUser = async () => {
      try {
        const token = localStorage.getItem('token');
        if (token) {
          api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
          const { data } = await api.get('/auth/me');
          setUser(data.user);
        }
      } catch (error) {
        console.error('Failed to load user', error);
        localStorage.removeItem('token');
        delete api.defaults.headers.common['Authorization'];
      } finally {
        setLoading(false);
      }
    };

    loadUser();
  }, []);

  // Login function
  const login = async (identifier, password) => {
    try {
      console.log('Sending login request with:', { identifier });
      const response = await api.post('/auth/login', { identifier, password });
      console.log('Login response:', response);
      
      if (!response || !response.data) {
        console.error('No data in response:', response);
        throw new Error('No data in server response');
      }
      
      const { data } = response;
      
      if (!data.token || !data.user) {
        console.error('Invalid response structure:', data);
        throw new Error('Invalid response from server');
      }
      
      const { token, user } = data;
      
      localStorage.setItem('token', token);
      api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      
      // Make sure we have all required user fields
      const userData = {
        id: user.id || user._id,
        email: user.email,
        firstName: user.firstName,
        lastName: user.lastName,
        phone: user.phone,
        role: user.role || 'user',
        userType: user.userType
      };
      
      console.log('Setting user data:', userData);
      setUser(userData);
      toast.success('Logged in successfully!');
      
      // Redirect based on user role
      const redirectTo = userData.role === 'admin' ? '/admin/dashboard' : '/dashboard';
      const from = location.state?.from?.pathname;
      console.log('Navigating to:', from || redirectTo);
      navigate(from || redirectTo, { replace: true });
      
      return { success: true, user: userData };
    } catch (error) {
      console.error('Login error details:', {
        message: error.message,
        response: error.response,
        data: error.response?.data,
        status: error.response?.status,
        headers: error.response?.headers
      });
      
      const message = error.response?.data?.message || error.message || 'Login failed. Please check your credentials and try again.';
      const errors = error.response?.data?.errors || [];
      
      // If there are field-specific errors, include them in the error object
      if (errors.length > 0) {
        const fieldErrors = errors.reduce((acc, err) => ({
          ...acc,
          [err.path || err.param || 'root']: err.msg || err.message || 'Invalid field'
        }), {});
        throw { message, errors: fieldErrors };
      }
      
      throw { 
        message,
        errors: [] 
      };
    }
  };

  // Register function
  const register = async (userData) => {
    try {
      const { data } = await api.post('/auth/register', userData);
      const { token, user } = data;
      
      localStorage.setItem('token', token);
      api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      
      setUser(user);
      toast.success('Registration successful!');
      
      // Redirect based on user role
      let redirectTo = '/dashboard'; // Default dashboard for regular users
      if (user.role === 'admin') {
        redirectTo = '/admin/dashboard'; // Admin dashboard
      }
      navigate(redirectTo, { replace: true }); // Always replace history for registration
      
      return { success: true, user };
    } catch (error) {
      const message = error.response?.data?.message || 'Registration failed. Please try again.';
      const errors = error.response?.data?.errors || [];
      toast.error(message);
      throw { message, errors }; // Throw the full error object
    }
  };

  // Logout function
  const logout = () => {
    localStorage.removeItem('token');
    delete api.defaults.headers.common['Authorization'];
    setUser(null);
    toast.success('Logged out successfully!');
    navigate('/login');
  };

  // Update user profile
  const updateProfile = async (userData) => {
    try {
      const { data } = await api.put('/auth/me', userData);
      setUser(data.user);
      toast.success('Profile updated successfully!');
      return data.user;
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to update profile';
      toast.error(message);
      throw error;
    }
  };

  // Change password
  const changePassword = async (currentPassword, newPassword) => {
    try {
      await api.put('/auth/change-password', { currentPassword, newPassword });
      toast.success('Password changed successfully!');
      return true;
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to change password';
      toast.error(message);
      throw error;
    }
  };

  // Forgot password
  const forgotPassword = async (email) => {
    try {
      await api.post('/auth/forgot-password', { email });
      toast.success('Password reset link sent to your email');
      return true;
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to send reset email';
      toast.error(message);
      throw error;
    }
  };

  // Reset password
  const resetPassword = async (token, newPassword) => {
    try {
      await api.post(`/auth/reset-password/${token}`, { newPassword });
      toast.success('Password reset successful. Please login with your new password.');
      navigate('/login');
      return true;
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to reset password';
      toast.error(message);
      throw error;
    }
  };

  // Check if user has specific role
  const hasRole = (role) => {
    if (!user) return false;
    return user.role === role;
  };

  // Check if user has any of the specified roles
  const hasAnyRole = (roles) => {
    if (!user) return false;
    return roles.includes(user.role);
  };

  const value = {
    user,
    loading,
    isAuthenticated: !!user,
    isAdmin: user?.role === 'admin',
    login,
    register,
    logout,
    updateProfile,
    changePassword,
    forgotPassword,
    resetPassword,
    hasRole,
    hasAnyRole,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export default AuthContext;
