import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import api from '../../services/api';
import { FiUpload, FiDownload, FiEdit2, FiSave, FiX } from 'react-icons/fi';

// Ethiopian regions and sub-cities
const REGIONS = ['Addis Ababa', 'Amhara', 'Oromia', 'Tigray', 'SNNPR', 'Afar', 'Somali', 'Benishangul-Gumuz', 'Gambela', 'Harari', 'Dire Dawa', 'Sidama', 'South West Ethiopia', 'South Ethiopia'];

const ADDIS_ABABA_SUB_CITIES = [
  'Addis Ketema', 'Akaki Kaliti', 'Arada', 'Bole', 'Gulele', 
  'Kirkos', 'Kolfe Keranio', 'Lideta', 'Nifas Silk-Lafto', 'Yeka'
];

const DOCUMENT_TYPES = {
  national_id: 'National ID',
  passport: 'Passport',
  driving_license: 'Driving License',
  other: 'Other'
};

const Profile = () => {
  const { id } = useParams();
  const { user: currentUser, updateUser } = useAuth();
  const [profileUser, setProfileUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [documentFile, setDocumentFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        if (!id || id === currentUser?._id) {
          setProfileUser(currentUser);
          setFormData({
            region: currentUser?.region || '',
            subCity: currentUser?.subCity || '',
            woreda: currentUser?.woreda || '',
            houseNumber: currentUser?.houseNumber || ''
          });
        } else {
          const response = await api.get(`/api/users/${id}`);
          setProfileUser(response.data);
        }
      } catch (error) {
        console.error('Error fetching user profile:', error);
        if (currentUser?._id) {
          navigate(`/dashboard/profile/${currentUser._id}`, { replace: true });
        } else {
          navigate('/dashboard');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, [id, currentUser, navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSave = async () => {
    try {
      const response = await api.put(`/api/users/${currentUser._id}`, formData);
      updateUser(response.data);
      setProfileUser(response.data);
      setEditing(false);
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  const handleDocumentUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('document', file);
    formData.append('documentType', 'national_id'); // Default to national ID

    try {
      setUploading(true);
      const response = await api.upload.uploadDocument(formData);
      // Update user's documents array
      const updatedUser = {
        ...profileUser,
        documents: [...(profileUser.documents || []), response.data]
      };
      setProfileUser(updatedUser);
      if (id === currentUser?._id) {
        updateUser(updatedUser);
      }
    } catch (error) {
      console.error('Error uploading document:', error);
    } finally {
      setUploading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (!profileUser) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-semibold text-gray-800">Profile not found</h1>
          <p className="mt-2 text-gray-600">The requested profile could not be loaded.</p>
        </div>
      </div>
    );
  }

  const isCurrentUser = id === currentUser?._id;
  const subCities = profileUser.region === 'Addis Ababa' ? ADDIS_ABABA_SUB_CITIES : [];

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      {/* Basic Information */}
      <div className="bg-white shadow overflow-hidden sm:rounded-lg">
        <div className="px-4 py-5 sm:px-6 flex justify-between items-center">
          <div>
            <h3 className="text-lg leading-6 font-medium text-gray-900">
              Personal Information
            </h3>
            <p className="mt-1 max-w-2xl text-sm text-gray-500">
              {isCurrentUser ? 'Your personal details' : 'User details'}
            </p>
          </div>
          {isCurrentUser && (
            <button
              onClick={() => editing ? handleSave() : setEditing(true)}
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              {editing ? (
                <>
                  <FiSave className="mr-2 h-4 w-4" />
                  Save Changes
                </>
              ) : (
                <>
                  <FiEdit2 className="mr-2 h-4 w-4" />
                  Edit Profile
                </>
              )}
            </button>
          )}
        </div>
        
        <div className="border-t border-gray-200 px-4 py-5 sm:p-0">
          <dl className="sm:divide-y sm:divide-gray-200">
            {/* Existing fields */}
            <div className="py-4 sm:py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
              <dt className="text-sm font-medium text-gray-500">Full name</dt>
              <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                {profileUser.firstName} {profileUser.lastName}
              </dd>
            </div>
            
            {/* Region */}
            <div className="py-4 sm:py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
              <dt className="text-sm font-medium text-gray-500">Region</dt>
              <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                {editing ? (
                  <select
                    name="region"
                    value={formData.region || ''}
                    onChange={handleInputChange}
                    className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm rounded-md"
                  >
                    <option value="">Select Region</option>
                    {REGIONS.map(region => (
                      <option key={region} value={region}>{region}</option>
                    ))}
                  </select>
                ) : (
                  profileUser.region || 'Not provided'
                )}
              </dd>
            </div>
            
            {/* Sub-city */}
            <div className="py-4 sm:py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
              <dt className="text-sm font-medium text-gray-500">Sub-city</dt>
              <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                {editing ? (
                  <div className="flex space-x-2">
                    <select
                      name="subCity"
                      value={formData.subCity || ''}
                      onChange={handleInputChange}
                      className="flex-1 min-w-0 block w-full px-3 py-2 rounded-md border border-gray-300 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                    >
                      <option value="">Select Sub-city</option>
                      {subCities.map(city => (
                        <option key={city} value={city}>{city}</option>
                      ))}
                    </select>
                    {!subCities.length && (
                      <input
                        type="text"
                        name="subCity"
                        value={formData.subCity || ''}
                        onChange={handleInputChange}
                        className="flex-1 min-w-0 block w-full px-3 py-2 rounded-md border border-gray-300 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                        placeholder="Enter sub-city"
                      />
                    )}
                  </div>
                ) : (
                  profileUser.subCity || 'Not provided'
                )}
              </dd>
            </div>
            
            {/* Woreda and House Number */}
            <div className="py-4 sm:py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
              <dt className="text-sm font-medium text-gray-500">Woreda</dt>
              <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                {editing ? (
                  <input
                    type="text"
                    name="woreda"
                    value={formData.woreda || ''}
                    onChange={handleInputChange}
                    className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                    placeholder="Enter woreda"
                  />
                ) : (
                  profileUser.woreda || 'Not provided'
                )}
              </dd>
            </div>
            
            <div className="py-4 sm:py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
              <dt className="text-sm font-medium text-gray-500">House Number</dt>
              <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                {editing ? (
                  <input
                    type="text"
                    name="houseNumber"
                    value={formData.houseNumber || ''}
                    onChange={handleInputChange}
                    className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                    placeholder="Enter house number"
                  />
                ) : (
                  profileUser.houseNumber || 'Not provided'
                )}
              </dd>
            </div>
            
            {/* Existing fields */}
            <div className="py-4 sm:py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
              <dt className="text-sm font-medium text-gray-500">Email address</dt>
              <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                {profileUser.email}
              </dd>
            </div>
            
            <div className="py-4 sm:py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
              <dt className="text-sm font-medium text-gray-500">Phone</dt>
              <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                {profileUser.phone || 'Not provided'}
              </dd>
            </div>
            {profileUser.bio && (
              <div className="py-4 sm:py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
                <dt className="text-sm font-medium text-gray-500">About</dt>
                <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                  {profileUser.bio}
                </dd>
              </div>
            )}
          </dl>
        </div>
      </div>

      {/* Documents Section */}
      <div className="bg-white shadow overflow-hidden sm:rounded-lg">
        <div className="px-4 py-5 sm:px-6">
          <h3 className="text-lg leading-6 font-medium text-gray-900">
            Identity Documents
          </h3>
          <p className="mt-1 max-w-2xl text-sm text-gray-500">
            {isCurrentUser ? 'Your uploaded identification documents' : 'User documents'}
          </p>
        </div>
        
        <div className="border-t border-gray-200 px-4 py-5 sm:p-6">
          {isCurrentUser && (
            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Upload New Document
              </label>
              <div className="mt-1 flex items-center">
                <input
                  type="file"
                  onChange={handleDocumentUpload}
                  className="hidden"
                  id="document-upload"
                  accept=".pdf,.jpg,.jpeg,.png"
                />
                <label
                  htmlFor="document-upload"
                  className="cursor-pointer bg-white py-2 px-3 border border-gray-300 rounded-md shadow-sm text-sm leading-4 font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 inline-flex items-center"
                >
                  <FiUpload className="mr-2 h-4 w-4" />
                  {uploading ? 'Uploading...' : 'Select File'}
                </label>
                <span className="ml-3 text-sm text-gray-500">
                  {documentFile ? documentFile.name : 'PDF, JPG, or PNG up to 5MB'}
                </span>
              </div>
            </div>
          )}
          
          {profileUser.documents?.length > 0 ? (
            <div className="overflow-hidden border border-gray-200 rounded-md">
              <ul className="divide-y divide-gray-200">
                {profileUser.documents.map((doc, index) => (
                  <li key={index} className="px-6 py-4 flex items-center justify-between">
                    <div className="flex items-center">
                      <div className="flex-shrink-0 h-10 w-10 rounded-full bg-blue-100 flex items-center justify-center">
                        <FiDownload className="h-5 w-5 text-blue-600" />
                      </div>
                      <div className="ml-4">
                        <p className="text-sm font-medium text-gray-900">
                          {DOCUMENT_TYPES[doc.documentType] || 'Document'} â€¢ {doc.documentNumber}
                        </p>
                        <p className="text-sm text-gray-500">
                          Uploaded {new Date(doc.uploadedAt).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                      doc.verified 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-yellow-100 text-yellow-800'
                    }`}>
                      {doc.verified ? 'Verified' : 'Pending Verification'}
                    </span>
                  </li>
                ))}
              </ul>
            </div>
          ) : (
            <div className="text-center py-8">
              <FiUpload className="mx-auto h-12 w-12 text-gray-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-900">No documents</h3>
              <p className="mt-1 text-sm text-gray-500">
                {isCurrentUser 
                  ? 'Upload your identification documents to verify your account.'
                  : 'No documents have been uploaded.'}
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
