import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiSend, FiPaperclip, FiMessageSquare, FiUser, FiArrowLeft } from 'react-icons/fi';
import { toast } from 'react-toastify';
import { useAuth } from '../../contexts/AuthContext';
import api from '../../services/api';
import LoadingSpinner from '../../components/common/LoadingSpinner';

const Messages = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [sending, setSending] = useState(false);
  const [conversation, setConversation] = useState(null);
  const [attachments, setAttachments] = useState([]);
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);
  const { user } = useAuth();
  const navigate = useNavigate();

  // Fetch conversation and messages
  useEffect(() => {
    const fetchConversation = async () => {
      try {
        setLoading(true);
        const { data } = await api.get('/api/messages/conversations');
        if (data.length > 0) {
          setConversation(data[0]);
          await fetchMessages(data[0]._id);
        }
      } catch (error) {
        console.error('Error fetching conversation:', error);
        toast.error('Failed to load messages');
      } finally {
        setLoading(false);
      }
    };

    fetchConversation();
  }, []);

  const fetchMessages = async (conversationId) => {
    try {
      const { data } = await api.get(`/api/messages/${conversationId}`);
      setMessages(data);
      scrollToBottom();
    } catch (error) {
      console.error('Error fetching messages:', error);
      toast.error('Failed to load messages');
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if ((!newMessage.trim() && attachments.length === 0) || sending) return;

    try {
      setSending(true);
      
      // Handle file uploads if any
      const uploadedAttachments = [];
      
      if (attachments.length > 0) {
        const formData = new FormData();
        attachments.forEach(file => {
          formData.append('files', file);
        });
        
        const { data } = await api.post('/api/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        
        uploadedAttachments.push(...data.files);
      }

      // Send the message
      await api.post('/api/messages', {
        content: newMessage.trim(),
        attachments: uploadedAttachments
      });

      setNewMessage('');
      setAttachments([]);
      
      // Refresh messages
      if (conversation) {
        await fetchMessages(conversation._id);
      }
    } catch (error) {
      console.error('Error sending message:', error);
      toast.error('Failed to send message');
    } finally {
      setSending(false);
    }
  };

  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    setAttachments(prev => [...prev, ...files]);
  };

  const removeAttachment = (index) => {
    setAttachments(prev => prev.filter((_, i) => i !== index));
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit',
      hour12: true 
    });
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner />
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col bg-white rounded-lg shadow overflow-hidden">
      {conversation ? (
        <>
          <div className="border-b border-gray-200 px-4 py-4 sm:px-6 flex items-center bg-white sticky top-0 z-10">
            <button 
              onClick={() => navigate(-1)} 
              className="md:hidden mr-2 p-1 rounded-full hover:bg-gray-100"
            >
              <FiArrowLeft className="h-5 w-5 text-gray-500" />
            </button>
            <div className="flex items-center">
              <div className="flex-shrink-0 h-10 w-10 rounded-full bg-blue-100 flex items-center justify-center">
                <FiUser className="h-5 w-5 text-blue-600" />
              </div>
              <div className="ml-3">
                <h3 className="text-base font-medium text-gray-900">
                  {conversation.participant?.firstName || 'Admin'}
                </h3>
                <p className="text-sm text-gray-500">
                  {conversation.unreadCount > 0 
                    ? `${conversation.unreadCount} unread message${conversation.unreadCount > 1 ? 's' : ''}` 
                    : 'Online'}
                </p>
              </div>
            </div>
          </div>

          <div className="flex-1 overflow-y-auto p-4 space-y-4">
            {messages.length === 0 ? (
              <div className="h-full flex items-center justify-center text-gray-500">
                <p>No messages yet. Start the conversation!</p>
              </div>
            ) : (
              messages.map((message) => (
                <div 
                  key={message._id} 
                  className={`flex ${message.sender._id === user.id ? 'justify-end' : 'justify-start'}`}
                >
                  <div 
                    className={`max-w-xs md:max-w-md lg:max-w-lg xl:max-w-xl rounded-lg px-4 py-2 ${
                      message.sender._id === user.id 
                        ? 'bg-blue-500 text-white rounded-br-none' 
                        : 'bg-gray-100 text-gray-800 rounded-bl-none'
                    }`}
                  >
                    <div className="text-sm">{message.content}</div>
                    
                    {message.attachments && message.attachments.length > 0 && (
                      <div className="mt-2 space-y-2">
                        {message.attachments.map((file, idx) => (
                          <div key={idx} className="bg-black bg-opacity-10 p-2 rounded">
                            <a 
                              href={file.url} 
                              target="_blank" 
                              rel="noopener noreferrer"
                              className="text-sm text-blue-600 hover:underline break-all"
                            >
                              {file.fileName || `Attachment ${idx + 1}`}
                            </a>
                          </div>
                        ))}
                      </div>
                    )}
                    
                    <div className={`text-xs mt-1 text-right ${
                      message.sender._id === user.id ? 'text-blue-100' : 'text-gray-500'
                    }`}>
                      {formatDate(message.createdAt)}
                    </div>
                  </div>
                </div>
              ))
            )}
            <div ref={messagesEndRef} />
          </div>

          <div className="border-t border-gray-200 p-4 bg-gray-50">
            {attachments.length > 0 && (
              <div className="flex flex-wrap gap-2 mb-3">
                {attachments.map((file, index) => (
                  <div 
                    key={index}
                    className="flex items-center bg-white rounded border border-gray-200 px-2 py-1 text-xs"
                  >
                    <span className="truncate max-w-xs">{file.name}</span>
                    <button 
                      onClick={() => removeAttachment(index)}
                      className="ml-2 text-red-500 hover:text-red-700"
                    >
                      &times;
                    </button>
                  </div>
                ))}
              </div>
            )}
            
            <form onSubmit={handleSendMessage} className="flex items-center">
              <div className="relative flex-1">
                <button
                  type="button"
                  onClick={() => fileInputRef.current?.click()}
                  className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                  title="Attach file"
                >
                  <FiPaperclip className="h-5 w-5" />
                  <input
                    type="file"
                    ref={fileInputRef}
                    onChange={handleFileSelect}
                    className="hidden"
                    multiple
                  />
                </button>
                <input
                  type="text"
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  placeholder="Type your message..."
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-l-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
              <button
                type="submit"
                disabled={(!newMessage.trim() && attachments.length === 0) || sending}
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-r-lg disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
              >
                {sending ? (
                  <span className="flex items-center">
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Sending...
                  </span>
                ) : (
                  <>
                    <FiSend className="h-5 w-5" />
                    <span className="sr-only">Send</span>
                  </>
                )}
              </button>
            </form>
          </div>
        </>
      ) : (
        <div className="h-full flex flex-col items-center justify-center p-6 text-center">
          <div className="mx-auto flex h-12 w-12 items-center justify-center rounded-full bg-blue-100">
            <FiMessageSquare className="h-6 w-6 text-blue-600" />
          </div>
          <h3 className="mt-2 text-sm font-medium text-gray-900">No messages</h3>
          <p className="mt-1 text-sm text-gray-500">
            You don't have any messages yet. Start a conversation with our support team.
          </p>
          <div className="mt-6">
            <button
              onClick={() => setConversation({ _id: 'new' })}
              className="inline-flex items-center px-4 py-2 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              <FiMessageSquare className="-ml-1 mr-2 h-5 w-5" />
              New Message
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Messages;
