import axios from 'axios';
import { toast } from 'react-hot-toast';

// Helper function to ensure paths start with /api
const ensureApiPrefix = (url) => {
  if (!url) return '/api';
  return url.startsWith('/api/') || url === '/api' ? url : `/api${url.startsWith('/') ? '' : '/'}${url}`;
};

// Create axios instance with base URL
const apiInstance = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000',
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 10000, // 10 seconds
  withCredentials: true, // For CORS with credentials
});

// Request interceptor
apiInstance.interceptors.request.use(
  (config) => {
    // Ensure the URL starts with /api
    config.url = ensureApiPrefix(config.url);
    
    console.log(`[API] ${config.method?.toUpperCase()} ${config.url}`, {
      data: config.data,
      params: config.params,
    });

    // Get token from localStorage
    const token = localStorage.getItem('token');
    
    // If token exists, add it to headers
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    
    return config;
  },
  (error) => {
    console.error('[API] Request error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor
apiInstance.interceptors.response.use(
  (response) => {
    // The server returns the data directly, not in a data property
    console.log(`[API] Response ${response.status} ${response.config.url}`, {
      data: response.data
    });
    
    // Return the response data directly
    return response.data;
  },
  (error) => {
    const errorData = {
      message: error.message,
      response: error.response ? {
        status: error.response.status,
        statusText: error.response.statusText,
        data: error.response.data,
      } : null,
      request: error.request ? 'Request was made but no response received' : 'No request was made',
      config: {
        url: error.config?.url,
        method: error.config?.method,
      },
    };

    console.error('[API] Response error:', errorData);

    // Only show error toast if there's a message and it's not a 401 (handled by interceptor)
    if (error.response?.status !== 401) {
      const errorMessage = error.response?.data?.message || error.message || 'An error occurred';
      toast.error(errorMessage);
    }

    // Return a rejected promise with the error
    return Promise.reject({
      message: error.response?.data?.message || error.message,
      errors: error.response?.data?.errors || []
    });
  }
);

// Upload images
const uploadImages = async (formData) => {
  try {
    const response = await apiInstance.post('/upload', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    return response.data;
  } catch (error) {
    throw error.response?.data || error.message;
  }
};

// Delete image
const deleteImage = async (publicId) => {
  try {
    const response = await apiInstance.delete(`/upload/${publicId}`);
    return response.data;
  } catch (error) {
    throw error.response?.data || error.message;
  }
};

// API methods
const api = {
  // Auth
  auth: {
    login: async (credentials) => {
      try {
        console.log('Making login request with:', credentials);
        const response = await apiInstance.post('/auth/login', {
          identifier: credentials.email, // Match the server's expected field name
          password: credentials.password
        });
        
        console.log('Login API response:', response);
        
        // The server returns { message, token, user }
        // We'll return it directly and let the component handle it
        return response;
      } catch (error) {
        console.error('Login API error:', error);
        // Ensure the error is properly propagated
        if (error.response) {
          throw {
            response: {
              data: error.response.data || { message: 'Login failed' },
              status: error.response.status
            }
          };
        }
        throw error;
      }
    },
    register: (userData) => apiInstance.post('/auth/register', userData),
    me: () => apiInstance.get('/auth/me'),
    updateProfile: (userData) => apiInstance.put('/auth/me', userData),
    changePassword: (data) => apiInstance.put('/auth/change-password', data),
    forgotPassword: (email) => apiInstance.post('/auth/forgot-password', { email }),
    resetPassword: (token, data) => apiInstance.post(`/auth/reset-password/${token}`, data),
  },
  
  // Users
  users: {
    getAll: (params) => apiInstance.get('/users', { params }),
    getById: (id) => apiInstance.get(`/users/${id}`),
    create: (userData) => apiInstance.post('/users', userData),
    update: (id, userData) => apiInstance.put(`/users/${id}`, userData),
    delete: (id) => apiInstance.delete(`/users/${id}`),
  },
  
  // Properties
  properties: {
    getAll: (params) => apiInstance.get('/properties', { params }),
    getById: (id) => apiInstance.get(`/properties/${id}`),
    create: (propertyData) => {
      const formData = new FormData();
      
      // Append all fields to form data
      Object.entries(propertyData).forEach(([key, value]) => {
        if (key === 'images' && Array.isArray(value)) {
          // Handle multiple image files
          value.forEach((file, index) => {
            formData.append('images', file);
          });
        } else if (value !== null && value !== undefined) {
          formData.append(key, value);
        }
      });
      
      return apiInstance.post('/properties', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
    },
    update: (id, propertyData) => {
      const formData = new FormData();
      
      // Append all fields to form data
      Object.entries(propertyData).forEach(([key, value]) => {
        if (key === 'images' && Array.isArray(value)) {
          // Handle multiple image files
          value.forEach((file, index) => {
            if (file instanceof File) {
              formData.append('newImages', file);
            } else {
              // It's an existing image URL or ID
              formData.append('existingImages', file);
            }
          });
        } else if (value !== null && value !== undefined) {
          formData.append(key, value);
        }
      });
      
      return apiInstance.put(`/properties/${id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
    },
    delete: (id) => apiInstance.delete(`/properties/${id}`),
    search: (params) => apiInstance.get('/properties/search', { params }),
    getByUser: (userId) => apiInstance.get(`/users/${userId}/properties`),
  },
  
  // Favorites
  favorites: {
    getAll: () => apiInstance.get('/favorites'),
    add: (propertyId) => apiInstance.post('/favorites', { propertyId }),
    remove: (propertyId) => apiInstance.delete(`/favorites/${propertyId}`),
    check: (propertyId) => apiInstance.get(`/favorites/check/${propertyId}`),
  },
  
  // Messages
  messages: {
    getAll: () => apiInstance.get('/messages'),
    getConversation: (userId) => apiInstance.get(`/messages/conversation/${userId}`),
    send: (data) => apiInstance.post('/messages', data),
    markAsRead: (messageId) => apiInstance.put(`/messages/${messageId}/read`),
    sendMessage: (messageData) => apiInstance.post('/messages', messageData),
    sendAdminReply: (messageData) => apiInstance.post('/messages/admin/reply', messageData),
    getMessages: (conversationId) => {
      // If it's in conversation_* format, use the direct endpoint
      if (conversationId.startsWith('conversation_')) {
        return apiInstance.get(`/messages/${conversationId}`);
      }
      // If it's a valid MongoDB ObjectId, use the conversation endpoint
      if (/^[0-9a-fA-F]{24}$/.test(conversationId)) {
        return apiInstance.get(`/messages/conversation/${conversationId}`);
      }
      // Default to direct endpoint
      return apiInstance.get(`/messages/${conversationId}`);
    },
    getConversations: () => apiInstance.get('/messages/conversations'),
  },
  
  // Interests
  interests: {
    submit: (interestData) => apiInstance.post('/api/interests', interestData),
    getAll: (params) => apiInstance.get('/api/interests', { params }),
    getMine: () => apiInstance.get('/api/interests/me'),
    updateStatus: (id, statusData) => apiInstance.put(`/api/interests/${id}/status`, statusData),
  },
  
  // Uploads
  uploads: {
    uploadImage: (file) => {
      const formData = new FormData();
      formData.append('image', file);
      
      return apiInstance.post('/uploads/images', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
    },
  },
  
  // Upload
  upload: {
    uploadImages,
    deleteImage,
  },
};

export default api;

export { apiInstance }; // Export the axios instance for custom requests
