import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  FiHome, 
  FiFileText, 
  FiSettings, 
  FiInfo, 
  FiHeart,
  FiClock,
  FiMessageSquare,
  FiEye,
  FiStar,
  FiList
} from 'react-icons/fi';
import { formatDistanceToNow } from 'date-fns';
import api from '../../services/api';

const Overview = () => {
  const [recentActivity, setRecentActivity] = useState([]);
  const [userInterests, setUserInterests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('recent');
  const [showAllInterests, setShowAllInterests] = useState(false);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        // Fetch user's interests
        const interestsResponse = await api.interests.getMine({ 
          limit: showAllInterests ? 100 : 5,
          populate: 'property'
        });
        setUserInterests(interestsResponse.data || []);

        // Fetch recent activities if not showing all interests
        if (!showAllInterests) {
          const activitiesResponse = await api.users.getActivities();
          setRecentActivity(activitiesResponse.data || []);
        }
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [showAllInterests]);

  const quickActions = [
    { 
      title: 'Post a Property', 
      description: 'List a new property for sale or rent',
      icon: FiHome,
      path: '/dashboard/create-property',
      color: 'bg-blue-100 text-blue-600'
    },
    { 
      title: 'View My Listings', 
      description: 'Manage your property listings',
      icon: FiFileText,
      path: '/dashboard/my-listings',
      color: 'bg-green-100 text-green-600'
    },
    { 
      title: showAllInterests ? 'Back to Overview' : 'My Interests', 
      description: showAllInterests ? 'Return to dashboard' : 'View all your saved interests',
      icon: showAllInterests ? FiHome : FiHeart,
      onClick: () => setShowAllInterests(!showAllInterests),
      color: showAllInterests ? 'bg-gray-100 text-gray-600' : 'bg-pink-100 text-pink-600'
    },
    { 
      title: 'Account Settings', 
      description: 'Update your profile and preferences',
      icon: FiSettings,
      path: '/dashboard/settings',
      color: 'bg-purple-100 text-purple-600'
    }
  ];

  const getActivityIcon = (activityType) => {
    switch (activityType) {
      case 'message':
        return <FiMessageSquare className="text-blue-500" />;
      case 'view':
        return <FiEye className="text-green-500" />;
      case 'favorite':
        return <FiStar className="text-yellow-500" />;
      case 'interest':
        return <FiHeart className="text-pink-500" />;
      default:
        return <FiInfo className="text-gray-500" />;
    }
  };

  const formatActivityTime = (timestamp) => {
    return formatDistanceToNow(new Date(timestamp), { addSuffix: true });
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold text-gray-900">
          {showAllInterests ? 'My Saved Interests' : 'Dashboard Overview'}
        </h2>
      </div>

      {/* Quick Actions */}
      <div className="mt-8">
        <h3 className="text-lg font-medium text-gray-900">
          {showAllInterests ? 'Quick Actions' : 'Quick Actions'}
        </h3>
        <div className="grid grid-cols-1 gap-6 mt-4 sm:grid-cols-2 lg:grid-cols-4">
          {quickActions.map((action, index) => (
            action.path ? (
              <Link
                key={index}
                to={action.path}
                className="flex items-start p-4 space-x-4 bg-white rounded-lg shadow hover:shadow-md transition-all duration-200"
              >
                <div className={`p-3 rounded-full ${action.color}`}>
                  <action.icon className="w-6 h-6" />
                </div>
                <div>
                  <h4 className="font-medium text-gray-900">{action.title}</h4>
                  <p className="text-sm text-gray-500">{action.description}</p>
                </div>
              </Link>
            ) : (
              <button
                key={index}
                onClick={action.onClick}
                className={`flex items-start w-full p-4 space-x-4 text-left bg-white rounded-lg shadow hover:shadow-md transition-all duration-200 ${action.color.replace('text-', 'hover:text-')}`}
              >
                <div className={`p-3 rounded-full ${action.color}`}>
                  <action.icon className="w-6 h-6" />
                </div>
                <div>
                  <h4 className="font-medium text-gray-900">{action.title}</h4>
                  <p className="text-sm text-gray-500">{action.description}</p>
                </div>
              </button>
            )
          ))}
        </div>
      </div>

      {/* Content Area */}
      {showAllInterests ? (
        <div className="mt-8">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-medium text-gray-900">My Saved Interests ({userInterests.length})</h3>
            <div className="flex space-x-2">
              <button
                onClick={() => window.location.reload()}
                className="p-2 text-gray-500 rounded-full hover:bg-gray-100"
                title="Refresh"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
                </svg>
              </button>
            </div>
          </div>
          
          {loading ? (
            <div className="flex items-center justify-center p-8">
              <div className="w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full animate-spin"></div>
              <span className="ml-2 text-gray-600">Loading your interests...</span>
            </div>
          ) : userInterests.length > 0 ? (
            <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3">
              {userInterests.map((interest) => (
                <div key={interest._id} className="overflow-hidden bg-white rounded-lg shadow">
                  {interest.property?.images?.[0] && (
                    <div className="h-48 overflow-hidden">
                      <img 
                        src={interest.property.images[0]} 
                        alt={interest.property.title}
                        className="object-cover w-full h-full"
                      />
                    </div>
                  )}
                  <div className="p-4">
                    <div className="flex items-start justify-between">
                      <h4 className="text-lg font-medium text-gray-900 line-clamp-1">
                        {interest.property?.title || 'Property'}
                      </h4>
                      <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                        interest.status === 'pending' ? 'bg-yellow-100 text-yellow-800' :
                        interest.status === 'approved' ? 'bg-green-100 text-green-800' :
                        interest.status === 'rejected' ? 'bg-red-100 text-red-800' :
                        'bg-gray-100 text-gray-800'
                      }`}>
                        {interest.status || 'Pending'}
                      </span>
                    </div>
                    <p className="mt-2 text-sm text-gray-600 line-clamp-2">
                      {interest.message || 'No additional message provided'}
                    </p>
                    <div className="flex items-center justify-between mt-4 text-sm text-gray-500">
                      <span>Submitted {formatDistanceToNow(new Date(interest.createdAt))} ago</span>
                      <Link 
                        to={`/properties/${interest.property?._id}`}
                        className="font-medium text-blue-600 hover:text-blue-500"
                      >
                        View Property
                      </Link>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="p-8 text-center bg-white rounded-lg shadow">
              <FiHeart className="w-12 h-12 mx-auto text-gray-400" />
              <h3 className="mt-2 text-lg font-medium text-gray-900">No interests yet</h3>
              <p className="mt-1 text-gray-500">
                {showAllInterests 
                  ? "You haven't shown interest in any properties yet."
                  : "Properties you're interested in will appear here."}
              </p>
              <div className="mt-6">
                <Link
                  to="/properties"
                  className="inline-flex items-center px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md shadow-sm hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                >
                  Browse Properties
                </Link>
              </div>
            </div>
          )}
        </div>
      ) : (
        <div className="mt-8">
          <div className="flex items-center justify-between">
            <h2 className="text-2xl font-bold text-gray-900">Dashboard Overview</h2>
          </div>

          {/* Quick Actions */}
          <div className="mt-8">
            <h3 className="text-lg font-medium text-gray-900">Quick Actions</h3>
            <div className="grid grid-cols-1 gap-6 mt-4 sm:grid-cols-2 lg:grid-cols-4">
              {quickActions.map((action, index) => (
                action.path ? (
                  <Link
                    key={index}
                    to={action.path}
                    className="flex items-start p-4 space-x-4 bg-white rounded-lg shadow hover:shadow-md transition-all duration-200"
                  >
                    <div className={`p-3 rounded-full ${action.color}`}>
                      <action.icon className="w-6 h-6" />
                    </div>
                    <div>
                      <h4 className="font-medium text-gray-900">{action.title}</h4>
                      <p className="text-sm text-gray-500">{action.description}</p>
                    </div>
                  </Link>
                ) : (
                  <button
                    key={index}
                    onClick={action.onClick}
                    className={`flex items-start w-full p-4 space-x-4 text-left bg-white rounded-lg shadow hover:shadow-md transition-all duration-200 ${action.color.replace('text-', 'hover:text-')}`}
                  >
                    <div className={`p-3 rounded-full ${action.color}`}>
                      <action.icon className="w-6 h-6" />
                    </div>
                    <div>
                      <h4 className="font-medium text-gray-900">{action.title}</h4>
                      <p className="text-sm text-gray-500">{action.description}</p>
                    </div>
                  </button>
                )
              ))}
            </div>
          </div>

          {/* My List & Recent Activity Tabs */}
          <div className="mt-8">
            <div className="border-b border-gray-200">
              <nav className="flex -mb-px space-x-8">
                <button
                  onClick={() => setActiveTab('recent')}
                  className={`py-4 px-1 border-b-2 font-medium text-sm ${
                    activeTab === 'recent'
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  Recent Activity
                </button>
                <button
                  onClick={() => setActiveTab('mylist')}
                  className={`py-4 px-1 border-b-2 font-medium text-sm ${
                    activeTab === 'mylist'
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  My List ({userInterests.length})
                </button>
              </nav>
            </div>

            <div className="mt-4">
              {activeTab === 'recent' ? (
                <div className="bg-white shadow overflow-hidden sm:rounded-md">
                  <ul className="divide-y divide-gray-200">
                    {loading ? (
                      <li className="px-6 py-4 text-center text-gray-500">
                        Loading activities...
                      </li>
                    ) : recentActivity.length > 0 ? (
                      recentActivity.map((activity) => (
                        <li key={activity._id} className="px-6 py-4">
                          <div className="flex items-center">
                            <div className="flex-shrink-0">
                              {getActivityIcon(activity.type)}
                            </div>
                            <div className="ml-4 flex-1">
                              <div className="flex items-center justify-between">
                                <p className="text-sm font-medium text-gray-900">
                                  {activity.message}
                                </p>
                                <div className="text-sm text-gray-500">
                                  <FiClock className="inline mr-1" />
                                  {formatActivityTime(activity.createdAt)}
                                </div>
                              </div>
                              {activity.metadata && (
                                <div className="mt-1 text-sm text-gray-500">
                                  {activity.metadata.details}
                                </div>
                              )}
                            </div>
                          </div>
                        </li>
                      ))
                    ) : (
                      <li className="px-6 py-4 text-center text-gray-500">
                        No recent activities found
                      </li>
                    )}
                  </ul>
                </div>
              ) : (
                <div className="bg-white shadow overflow-hidden sm:rounded-md">
                  {loading ? (
                    <div className="px-6 py-4 text-center text-gray-500">
                      Loading your interests...
                    </div>
                  ) : userInterests.length > 0 ? (
                    <ul className="divide-y divide-gray-200">
                      {userInterests.map((interest) => (
                        <li key={interest._id} className="px-6 py-4">
                          <div className="flex items-center">
                            {interest.property?.images?.[0] && (
                              <div className="flex-shrink-0 h-16 w-16">
                                <img
                                  className="h-16 w-16 rounded-md object-cover"
                                  src={interest.property.images[0]}
                                  alt={interest.property.title}
                                />
                              </div>
                            )}
                            <div className="ml-4 flex-1">
                              <div className="flex items-center justify-between">
                                <h4 className="text-sm font-medium text-gray-900">
                                  {interest.property?.title || 'Property'}
                                </h4>
                                <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                                  {interest.status || 'Pending'}
                                </span>
                              </div>
                              <div className="mt-1 text-sm text-gray-500">
                                {interest.message && (
                                  <p className="truncate">{interest.message}</p>
                                )}
                                <p className="text-xs text-gray-400 mt-1">
                                  Submitted {formatActivityTime(interest.createdAt)}
                                </p>
                              </div>
                            </div>
                          </div>
                        </li>
                      ))}
                    </ul>
                  ) : (
                    <div className="px-6 py-8 text-center">
                      <FiHeart className="mx-auto h-12 w-12 text-gray-400" />
                      <h3 className="mt-2 text-sm font-medium text-gray-900">No interests yet</h3>
                      <p className="mt-1 text-sm text-gray-500">
                        Properties you're interested in will appear here.
                      </p>
                      <div className="mt-6">
                        <Link
                          to="/properties"
                          className="inline-flex items-center px-4 py-2 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                        >
                          Browse Properties
                        </Link>
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Overview;
